INSERT INTO `articles` (`article_id`, `article_linkid`, `article_where`, `article_title`, `article_content`, `keywords`, `description`, `updated`, `widget`, `widget_url`) VALUES
(1, 108, 'help', 'CleanTalk Help. Manuals, Questions and Answers.', '<h1 class="help-content__title --1">CleanTalk Help. Manuals, Questions and Answers.</h1>\n<p>&nbsp;</p>\n<h4>Anti-Spam Service</h4>\n<div class="help-content__row">\n<div class="help-content__right">\n<ul>\n<li><a href="/help/premium-notice">Premium Notice is still shown in WordPress backend after payment</a></li>\n<li><a href="/help/faq#test">How can I test the Anti-Spam on the website?</a></li>\n<li><a href="/help/faq#more_spam">After the installation, I noticed in the statistics that the number of spam attacks had increased</a></li>\n<li><a href="/help/forbidden-please-enable-javascript">I see in logs a lot of blocked messages with reason "Forbidden. Please enable JavaScript. Spam sender name."</a></li>\n<li><a href="/help/jcomments-spam">Why CleanTalk Doesn''t Stop Spam on JComments?</a></li>\n<li><a href="/help/GreyList">What is Grey List?</a></li>\n<li><a href="/help/WordPress-plugin-settings">WordPress: The CleanTalk Anti-Spam Plugin Settings</a></li>\n<li><a href="/help/deletion-from-public-page">How to delete your IP or e-mail from CleanTalk Blacklist public page?</a></li>\n</ul>\n<p>&nbsp;</p>\n<h4>Anti-Spam Package And Payments</h4>\n<ul class="help-content__list">\n<li><a href="/help/faq#need_more">Can I have a more powerful package?</a></li>\n<li><a href="/help/faq#double_connect">I want to connect two resources to the service. Do I have to register two accounts, or I can use the same key?</a></li>\n<li><a href="/help/invoice-with-vat">Can I have an invoice with VAT?</a></li>\n<li><a href="/help/extra-package">CleanTalk Extra Package</a></li>\n<li><a href="/help/faq#not_connected">My website status is "Not connected"!</a></li>\n<li><a href="/help/licence-for-multisite-environment-wpmu">What type of license use for WordPress Multisite environment (WPMU)?</a></li>\n<li><a title="The CleanTalk Affiliate Program" href="/help/affiliate-program">Does CleanTalk have an Affiliate Program?</a></li>\n<li><a href="/usage/hosting-antispam">Hosting Anti-Spam Package</a></li>\n</ul>\n<p>&nbsp;</p>\n<h4>Account&nbsp;Management</h4>\n<ul class="help-content__list">\n<li><a href="/help/access-to-dashboard">How to restore access to my Dashboard?</a></li>\n<li><a href="/help/faq#feedback_spam">How do I report a missed spam bot or incorrect filter?</a></li>\n<li><a href="/help/i-cant-find-message">I can''t find the message in the control Panel of the website</a></li>\n<li><a href="/help/my-ip-blacklisted">My IP blacklisted</a></li>\n<li><a href="/help/I-want-to-block-ip">I want to block IP/email/IP network</a></li>\n<li><a href="/help/store-the-content-of-messages">I don''t want to store the content of messages in the logs</a></li>\n<li><a href="/help/change-service-url">How to move service to another website?</a></li>\n<li><a href="/help/blacklist-usage">How to use personal blacklists?</a></li>\n<li><a href="/help/stop-word">Stop-Word Feature</a></li>\n<li><a href="/help/sfw-blocks-networks">SpamFireWall - Subnet Blocking</a></li>\n<li><a href="/help/delegation">Website delegation to other users</a></li>\n<li><a href="/help/Anti-Spam-Log">How to use Anti-Spam Log</a></li>\n<li><a href="/help/spam-comment-management">Spam Comment Management</a></li>\n<li><a href="/help/overwrite-message-for-blocked-visitors">How to overwrite message for blocked visitors</a></li>\n</ul>\n<p>&nbsp;</p>\n<h4>Other Questions</h4>\n<ul class="help-content__list">\n<li><a title="Review  About CleanTalk" href="/help/review-help-main">How Do I Post a Review About Cleantalk?</a></li>\n<li><a title="Is CleanTalk Compatible with CloudFlare?" href="/help/cleantalk-cloudflare">Is CleanTalk Compatible with CloudFlare?</a></li>\n<li><a title="CleanTalk vs Akismet" href="/help/cleantalk-vs-akismet">Is CleanTalk Better than Akismet?</a></li>\n</ul>\n<p>&nbsp;</p>\n<h4>Security Service</h4>\n<ul>\n<li><a href="/help/FAQ-security">Security service FAQ</a></li>\n<li><a href="/help/Security-Log">How to use Security Log</a></li>\n<li><a href="/help/Security-Firewall">Security Firewall</a></li>\n<li><a href="/help/security-malware-scanner">Malware Scanner</a></li>\n</ul>\n<p>&nbsp;</p>\n<div class="help-ways">\n<h3 class="help-ways__title">How to get help?</h3>\n<ul class="help-ways__list">\n<li class="help-ways__item --1">\n<div class="help-ways__item__title"><a href="/my/support">Technical support</a></div>\n<div class="help-ways__item__content">\n<div class="help-ways__item__title">Create a support ticket</div>\n</div>\n</li>\n<li class="help-ways__item --1">\n<div class="help-ways__item__title"><a class="help-ways__item__link" href="/forum/">Support board</a></div>\n<div class="help-ways__item__content">Ask a question on our support board</div>\n</li>\n<li class="help-ways__item --2">\n<div class="help-ways__item__title"><a class="help-ways__item__link" href="/blog">Blog</a></div>\n<div class="help-ways__item__content">Get news on our blog</div>\n</li>\n</ul>\n</div>\n</div>\n</div>', 'Cleantalk, SpamFireWall, Anti-spam, Setup manual, manual, help, faq', 'CleanTalk Help. Manuals, Questions and Answers.', '2018-04-06 18:40:05', NULL, NULL),
(2, 0, 'docs', 'docsmain', '<div class="panel panel-default">\n<div class="panel-heading">В помощь техподдержке</div>\n<div class="panel-body"><a href="docs?action=view_docs&amp;article_id=263">Полезные фразы на английском</a></div>\n</div>\n<div class="panel panel-default">\n<div class="panel-heading">Интерфейс управления антиспам приложениями</div>\n<div class="panel-body"><a href="docs?action=view_docs&amp;article_id=14">Добавление нового приложения</a></div>\n</div>\n<div class="panel panel-default">\n<div class="panel-heading">Чеклисты</div>\n<div class="panel-body"><a href="docs?action=view_docs&amp;article_id=17">Чеклист запуска новой вебноды (WEBNODE_X)</a></div>\n</div>\n<div class="panel panel-default">\n<div class="panel-heading">SpamFireWall</div>\n<div class="panel-body"><a href="docs?action=view_docs&amp;article_id=23">Описание и принципы работы</a></div>\n</div>\n<div class="panel panel-default">\n<div class="panel-heading">Почтовые рассылки</div>\n<div class="panel-body"><a href="docs?action=view_docs&amp;article_id=35">Генерация рассылок</a></div>\n</div>\n<div class="panel panel-default">\n<div class="panel-heading">Схема данных</div>\n<div class="panel-body"><a href="docs?action=view_docs&amp;article_id=79">Типы данных в таблицах и отчётах</a></div>\n</div>\n<div class="panel panel-default">\n<div class="panel-heading">Оформление элементов ПУ и NOC</div>\n<div class="panel-body"><a href="docs?action=view_docs&amp;article_id=341">Оформление элементов</a></div>\n</div>', '', '', '2017-10-25 11:42:21', NULL, NULL),
(3, 0, 'docs', 'Check it', '<p>beginphp</p>\r\n<p>$this-&gt;page_info[''docs''] = $docs;<br />&nbsp;$this-&gt;link-&gt;template = ''docs.html'';<br />&nbsp;&nbsp;&nbsp; $this-&gt;page_info[''head''][''title''] = ''Документация''; <br />&nbsp;&nbsp; &nbsp;&nbsp; $this-&gt;display();</p>\r\n<p>endphp</p>\r\n<p>&nbsp;</p>\r\n<p>beginplain</p>\r\n<p>Hi<br /><br />why</p>\r\n<p>endplain</p>\r\n<p>&nbsp;</p>\r\n<p>beginjs</p>\r\n<p>$(''.closeticket'').click(function()<br />&nbsp;&nbsp;&nbsp; {<br />&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $ticket_id = this.id;<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(''#closed_'' + $ticket_id).show();<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(''#opened_'' + $ticket_id).hide();&nbsp; &nbsp;<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $.ajax({ <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; url: "/noc/tickets?action=close_ticket",<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; type: "GET",<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; data: ({ ticket_id : $ticket_id<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }),<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; success: function(data)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {&nbsp; &nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; });<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; });</p>\r\n<p>endjs</p>', '', '', '2017-10-25 11:42:21', NULL, NULL),
(4, 110, 'help', 'I Can''t Find the Message in the Control Panel of the Website', '<h1 style="text-align: left;">I Can''t Find the Message in the Control Panel of the Website</h1>\n<p>&nbsp;</p>\n<p>1. If you see in the logs of the service that you have approved comments, signups or messages from the contact form, but you have not received the notification or cannot find it in the control Panel of the website. Take a look at the log the service on those form fields that were filled in by the user, most likely one or more fields were either not filled or filled incorrectly. In this case, your form of comments gives the visitor an error saying that the form are incomplete and need to repeat data entry.</p>\n<p>CleanTalk processes the request before the form, so the query will be displayed in the logs, but in the control Panel of the site this request will not be because the user was unable to send it.</p>\n<p>So you always get such messages via CleanTalk, even if the user refused to complete the form again. Perhaps you find that your form of a lot of fields and users are not convenient to fill some of them and remove the extra field that can increase your conversions.</p>\n<p>&nbsp;</p>\n<p>2. For contact forms, and forms for sending of the notification email. Check your Spam folder in your mail, it is possible that the desired message has been moved to the Spam folder by your mail server. <a class="daria-goto-anchor" href="https://www.google.com/webhp?sourceid=chrome-instant&amp;ion=1&amp;espv=2&amp;ie=UTF-8#newwindow=1&amp;safe=off&amp;q=emails+from+my+website+go+to+spam" target="_blank" data-vdir-href="https://mail.yandex.ru/re.jsx?uid=1130000011801443&amp;h=a,3iMZhreLKmFUa-zulgC0wg&amp;l=aHR0cHM6Ly93d3cuZ29vZ2xlLmNvbS93ZWJocD9zb3VyY2VpZD1jaHJvbWUtaW5zdGFudCZpb249MSZlc3B2PTImaWU9VVRGLTgjbmV3d2luZG93PTEmc2FmZT1vZmYmcT1lbWFpbHMrZnJvbStteSt3ZWJzaXRlK2dvK3RvK3NwYW0" data-orig-href="https://www.google.com/webhp?sourceid=chrome-instant&amp;ion=1&amp;espv=2&amp;ie=UTF-8#newwindow=1&amp;safe=off&amp;q=emails+from+my+website+go+to+spam">To learn more about this question in the search.</a>&nbsp;</p>\n<p>&nbsp;</p>', 'message, CleanTalk, Control Panel, Dashboard', 'Can''t Find the Message in the Control Panel of the Website', '2017-10-25 11:42:21', NULL, NULL),
(5, 111, 'help', 'My IP is blacklisted', '<h1>My IP is blacklisted</h1>\n<p>&nbsp;</p>\n<p>CleanTalk logs all requests on your web site, including the ones when you test service, so your IP is shown in your anti-spam log, but herewith your IP is not BlackListed. You can always check any IP here: [&nbsp;<a class="daria-goto-anchor" href="/blacklists" target="_blank" data-vdir-href="https://mail.yandex.ru/re.jsx?uid=1130000011801443&amp;h=a,CTxE_ep0fqelLK6bPhyYWg&amp;l=aHR0cHM6Ly9jbGVhbnRhbGsub3JnL2JsYWNrbGlzdHM" data-orig-href="https://cleantalk.org/blacklists">https://cleantalk.org/bla<wbr />cklists</a>&nbsp;].</p>\n<p>&nbsp;</p>', 'blacklisted IP, blacklist, IP', 'My IP is blacklisted', '2017-10-25 11:42:21', NULL, NULL),
(6, 112, 'help', 'I Want to Block IP/Email', '<h1>I Want to Block IP/Email/IP network</h1>\n<p>&nbsp;</p>\n<p>To use filters for AntiSpam, you need to log in to the <a href="/my">CleanTalk Dashboard</a> and then:</p>\n<p>&nbsp;</p>\n<h2>1. Go to personal lists of a particular service</h2>\n<p>&nbsp;</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/I-want-to-block-ip/service.png" alt="" width="1000" height="258" /></p>\n<h2>&nbsp;</h2>\n<h2>2. Choose the Antispam service</h2>\n<p>&nbsp;</p>\n<p>&nbsp;AntiSpam (default) - will blocks users from selected IP or network from using contacts/messages/registrations/comments forms.</p>\n<p>&nbsp;</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/I-want-to-block-ip/personal.png" alt="" width="1000" height="410" /></p>\n<h2>&nbsp;</h2>\n<h2>3. Add a new filter and select its type</h2>\n<p>&nbsp;</p>\n<p>You can select the following filter types for Antispam:</p>\n<p>- IP address</p>\n<p>- E-mail</p>\n<p>- Country</p>\n<p>- Domain</p>\n<p>- IP network</p>\n<p>- Stop word</p>\n<p>&nbsp;</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/I-want-to-block-ip/addfilt.png" alt="" width="1000" height="267" /></p>\n<p>&nbsp;</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/I-want-to-block-ip/filttype.png" alt="" width="1000" height="247" /></p>\n<h2>&nbsp;</h2>\n<h2>4. Filtering by IP address</h2>\n<p>&nbsp;</p>\n<p>If you want to blacklist or whitelist an IP address, select the appropriate filter, then enter one or more IP addresses and specify a rule (Blacklist or Whitelist). As a result, records will be added to the table.</p>\n<p>&nbsp;</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/I-want-to-block-ip/ipfilt.png" alt="" width="1000" height="273" /></p>\n<p>&nbsp;</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/I-want-to-block-ip/ipresult.png" alt="" width="1000" height="142" /></p>\n<h2>&nbsp;</h2>\n<h2>5. Filtering by E-mail</h2>\n<p>&nbsp;</p>\n<p>If you want to blacklist or whitelist an E-mail address, select the appropriate filter, then enter one or more E-mail addresses and specify a rule (Blacklist or Whitelist). As a result, records will be added to the table.</p>\n<p>&nbsp;</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/I-want-to-block-ip/emailfilt.png" alt="" width="1000" height="346" /></p>\n<p>&nbsp;</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/I-want-to-block-ip/emailresult.png" alt="" width="1000" height="137" /></p>\n<h2>&nbsp;</h2>\n<h2>6. Filtering by country</h2>\n<p>&nbsp;</p>\n<p>If you want to blacklist or whitelist countries, select the appropriate filter, then select one or more countries from the list, specify a rule, and click <strong>Add</strong>. As a result, the records will be added to the table.</p>\n<p>&nbsp;</p>\n<p>Rules for countries:</p>\n<p><strong>Whitelist</strong> - selected countries will be allowed and added to the list</p>\n<p><strong>Blacklist</strong> - selected countries will be banned and added to the list</p>\n<p><strong>Blacklist all countries except selected</strong> - all other countries will be blacklisted and added to the list</p>\n<p><strong>Whitelist all countries except selected</strong> - all other countries will be whitelisted and added to the list</p>\n<p>&nbsp;</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/I-want-to-block-ip/countryfilt.png" alt="" width="1000" height="257" /></p>\n<p>&nbsp;</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/I-want-to-block-ip/countryresult.png" alt="" width="1000" height="147" /></p>\n<h2>&nbsp;</h2>\n<h2>7. Filtering by domain</h2>\n<p>&nbsp;</p>\n<p>If you want to blacklist or whitelist a specific mail domain, select the appropriate filter, then enter one or more domain addresses and specify a rule (Blacklist or Whitelist). As a result, records will be added to the table.</p>\n<p>&nbsp;</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/I-want-to-block-ip/domenfilt.png" alt="" width="1000" height="280" /></p>\n<p>&nbsp;</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/I-want-to-block-ip/domenresult.png" alt="" width="1000" height="137" /></p>\n<h2>&nbsp;</h2>\n<h2>8. Filtering by IP network</h2>\n<p>&nbsp;</p>\n<p>If you want to blacklist or whitelist specific IP network, select the appropriate filter, then enter one or more subnets and specify a rule (Blacklist or Whitelist). As a result, records will be added to the table.</p>\n<p>Subnet blocking:</p>\n<blockquote>\n<p>1.2.3.4 - IP address</p>\n<p>/16 - subnet mask</p>\n</blockquote>\n<p>If you don''t know how it works simply put numbers for mask divisible by 8:</p>\n<ul>\n<li>1.2.3.4/8 - all IP addresses starting from 1.n.n.n (from 1.0.0.0 to 1.255.255.255) will be blocked.</li>\n<li>1.2.3.4/16 - all IP addresses starting from 1.2.n.n (from 1.2.0.0 to 1.2.255.255) will be blocked.</li>\n<li>1.2.3.4/24 - all IP addresses starting from 1.2.3.n (from 1.2.3.0 to 1.2.3.255) will be blocked.</li>\n</ul>\n<p>&nbsp;</p>\n<p><strong>You can read about subnets here </strong>&nbsp;<a href="/sfw-blocks-networks-extended">https://cleantalk.org/sfw-blocks-networks-extended</a></p>\n<p>&nbsp;</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/I-want-to-block-ip/ipsubnetfilt.png" alt="" width="1000" height="261" /></p>\n<p>&nbsp;</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/I-want-to-block-ip/ipsubnetresult.png" alt="" width="1000" height="129" /></p>\n<h2>&nbsp;</h2>\n<h2>9. Filtering by stop words</h2>\n<p>&nbsp;</p>\n<p>You can block comments containing &ldquo;Stop words&rdquo; to enhance spam filtering and block messages containing obscene words. You can add both specific words and phrases.</p>\n<p>&nbsp;</p>\n<p>You can read more here:&nbsp;<a href="/help/introduction#stopwords_block">https://cleantalk.org/help/introduction#stopwords_block</a></p>\n<p>&nbsp;</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/I-want-to-block-ip/stopwfilt.png" alt="" width="1000" height="273" /></p>\n<p>&nbsp;</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/I-want-to-block-ip/stopwresult.png" alt="" width="1000" height="150" /></p>\n<h2>&nbsp;</h2>\n<h2>10. Go to the plugin settings in your website dashboard and save settings</h2>\n<p>&nbsp;</p>\n<p>It is essential to changes take effect even if you haven''t change the settings. If you don''t do this, changes will take effect only in 24 hours.</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p><strong>Note 1:&nbsp;</strong>If you want to block any IP/email and block access to comments/register, just go to <a href="/my">Cleantalk Dashboard</a>&nbsp;&mdash;&gt; Log, find the necessary request and mark it as "Spam". This IP/email will be added to your personal blacklist.</p>\n<p>&nbsp;</p>\n<p>More details are here: [ <a href="/faq#feedback_spam">https://cleantalk.org/faq#feedback_spam</a> ].</p>\n<p>&nbsp;</p>\n<p><strong>Note 2:</strong> You can use masks when specifying filters. Symbol asterisk "<strong>*</strong>" means any set of characters. *@mail.com - will block any address ending by @mail.com</p>\n<p>&nbsp;Examples:</p>\n<blockquote>\n<p>&nbsp; &bull; <strong>name*@example.com</strong>&nbsp;(all e-mail addresses with any set of characters after <strong>"name"</strong> will be blocked).</p>\n<p>&nbsp; &bull; <strong>*aa44@example.com</strong>&nbsp;(all e-mail addresses with any set of characters before&nbsp;<strong>"aa44"</strong> will be blocked).</p>\n<p>&nbsp; &bull; <strong>*kkk*vvv*@example.com</strong>&nbsp;(all e-mail addresses with any set of characters before <strong>"kkk"</strong>, before <strong>"vvv"</strong> and after <strong>"vvv"</strong> will be blocked).</p>\n<p>&nbsp;&bull; <strong>*@*example*</strong>&nbsp;(all addresses with any set of characters before and after <strong>"example"</strong>&nbsp;in domain name will be blocked).</p>\n<p>&nbsp; &bull; And so on, you can use any combinations of characters and asterisks to make a mask.</p>\n</blockquote>\n<p>&nbsp;</p>\n<p><strong>If you still have questions you can always ask our Tech support by&nbsp;creating a ticket&nbsp;<a href="/my/support">https://cleantalk.org/my/support</a> or mailing to&nbsp;<a href="mailto:welcome@cleantalk.org">welcome@cleantalk.org</a>.</strong></p>', 'blacklist, block, IP, email', 'I Want to Block IP/Email', '2018-04-06 19:03:49', NULL, NULL),
(183, 279, 'help', '1С Битрикс: Как вручную обновить антиспам решение от CleanTalk', '<h1>1С Битрикс: Как вручную обновить антиспам решение от CleanTalk</h1>\n<p>&nbsp;</p>\n<h3>Текстовая версия</h3>\n<ol>\n<li>Зайдите в Панель Администратора 1С Битрикс &mdash;&gt; Marketplace &mdash;&gt; Установленные решения &mdash;&gt; тройное равно рядом с названием "Антиспам без CAPTCHA от CleanTalk" &mdash;&gt; Удалить.</li>\n<li>После успешного удаления на следующей странице нажмите на кнопку "<strong>Вернуться в список</strong>".</li>\n<li>Ещё раз нажмите на тройное равно рядом с названием "Антиспам без CAPTCHA от CleanTalk" &mdash;&gt; Стереть. Подтвердите стирание.</li>\n<li>Скачайте архив с решением от CleanTalk (ссылка в начале этой страницы или по выданной Вам ссылке лично), распакуйте скачанный архив. После распаковки вы увидите два архива с разными кодировками. Выберите нужный и распакуйте из него папку "cleantalk.antispam". Поместите полученную папку в "<strong>&lt;корневая-папка-1С-Битрикс&gt;/bitrix/modules</strong>". В итоге должен получиться путь "<strong>&lt;корневая-папка-1С-Битрикс&gt;/bitrix/modules/cleantalk.antispam</strong>".</li>\n<li>Вернитесь в "Marketplace &mdash;&gt; Установленные решения" и обновите страницу. В таблице "Доступные решения" снова появится решение "Антиспам без CAPTCHA от CleanTalk". Нажмите на тройное равно рядом с названием решения &mdash;&gt; Установить.</li>\n<li>После успешной установки перейдите в Настройки &mdash;&gt; Настройки продукта &mdash;&gt; Настройки модулей. В выпадающем списке выберите "Антиспам без CAPTCHA от CleanTalk".</li>\n<li>Настройте плагин, проверьте, что ключ доступа совпадает с ключом из Вашей <a title="Панель управления CleanTalk" href="http://cleantalk.org/my/" target="_blank">панели управления CleanTalk</a>. Затем нажмите на кнопку "<strong>Сохранить</strong>".</li>\n<li>(Необязательно) Проверьте защиту с помощью пробной регистрации с почты из чёрного списка <strong>stop_email@example.com</strong>.</li>\n</ol>\n<p>&nbsp;</p>\n<h2 style="text-align: center;">Поздравляем, обновление завершено!</h2>\n<p>&nbsp;</p>\n<h3>Версия с картинками<br /></h3>\n<p>1. Зайдите в Панель Администратора 1С Битрикс &mdash;&gt; Marketplace &mdash;&gt; Установленные решения &mdash;&gt; тройное равно рядом с названием "Антиспам без CAPTCHA от CleanTalk" &mdash;&gt; Удалить.</p>\n<p><img src="http://i.imgur.com/h50YsnW.png" alt="" width="900" height="647" /></p>\n<p>&nbsp;</p>\n<p>2. После успешного удаления на следующей странице нажмите на кнопку "<strong>Вернуться в список</strong>".</p>\n<p><img src="http://i.imgur.com/FsDQtwt.png" alt="" width="900" height="332" /></p>\n<p>&nbsp;</p>\n<p>3. Ещё раз нажмите на тройное равно рядом с названием "Антиспам без CAPTCHA от CleanTalk" &mdash;&gt; Стереть. Подтвердите стирание.</p>\n<p><img src="http://i.imgur.com/kR3vI5d.png" alt="" width="900" height="647" /></p>\n<p>&nbsp;</p>\n<p>4. Скачайте архив с решением от CleanTalk (ссылка в начале этой страницы или по выданной Вам ссылке лично), распакуйте скачанный архив. После распаковки вы увидите два архива с разными кодировками. Выберите нужный и распакуйте из него папку "cleantalk.antispam". Поместите полученную папку в "<strong>&lt;корневая-папка-1С-Битрикс&gt;/bitrix/modules</strong>". В итоге должен получиться путь "<strong>&lt;корневая-папка-1С-Битрикс&gt;/bitrix/modules/cleantalk.antispam</strong>".</p>\n<p>5. Вернитесь в "Marketplace &mdash;&gt; Установленные решения" и обновите страницу. В таблице "Доступные решения" снова появится решение "Антиспам без CAPTCHA от CleanTalk". Нажмите на тройное равно рядом с названием решения &mdash;&gt; Установить.</p>\n<p><img src="http://i.imgur.com/XonEtUo.png" alt="" width="900" height="647" /></p>\n<p>&nbsp;</p>\n<p>6. После успешной установки перейдите в Настройки &mdash;&gt; Настройки продукта &mdash;&gt; Настройки модулей. В выпадающем списке выберите "Антиспам без CAPTCHA от CleanTalk".</p>\n<p><img src="http://i.imgur.com/GnZbi6o.png" alt="" width="900" height="647" /></p>\n<p>&nbsp;</p>\n<p>7. Настройте плагин, проверьте, что ключ доступа совпадает с ключом из Вашей <a title="Панель управления CleanTalk" href="http://cleantalk.org/my/" target="_blank">панели управления CleanTalk</a>. Затем нажмите на кнопку "<strong>Сохранить</strong>".</p>\n<p>&nbsp;<img src="http://i.imgur.com/qOHxpN8.png" alt="" width="900" height="647" /></p>\n<p>&nbsp;</p>\n<h2 style="text-align: center;">Поздравляем, обновление завершено!</h2>\n<div>Статус антиспама в Панели управления изменится в течение 3-5 минут после обновления.</div>\n<div>Если возникнут какие-либо вопросы по работе нашего плагина, то Вы можете задать их через:</div>\n<div>&nbsp;&mdash; Почту [ support@cleantalk.org ].</div>\n<div>&nbsp;&mdash; Обращение [ <a href="/my/support" target="_blank">https://cleantalk.org/my/support</a> ].</div>\n<div>&nbsp;&mdash; Форум [ <a href="https://community.cleantalk.org/" target="_blank">https://community.cleantalk.org/</a> ].</div>\n<p>&nbsp;</p>\n<div class="button_go"><a class="button --blue" href="/my">Перейти в панель управления</a></div>\n<p>&nbsp;</p>\n<div class="grey_text">Посмотреть статус антиспама, добавить новый сайт или управлять существующими вы можете в Панели управления.</div>', 'обновление, обновить, вручную, инструкция, решение, модуль, cleantalk, 1C Битрикс, битрикс, bitrix', '1С Битрикс: Как вручную обновить антиспам решение от CleanTalk', '2017-10-25 11:42:21', NULL, NULL),
(19, 121, '0', 'Installing CleanTalk on WordPress network (WPMU)', '<p>1. Firstly, be sure you configured network according official WordPress manual: <a href="https://codex.wordpress.org/Create_A_Network">https://codex.wordpress.org/Create_A_Network</a></p>\r\n<p>2.&nbsp;Install CleanTalk: In top left menu select "My sites" -&gt; "Network admin" -&gt; "Plugins" -&gt; "Add new" -&gt; type ''cleantalk''</p>\r\n<p><img src="../images/screens/en/wp_find_cleantalk.png" alt="" width="1024" height="548" /></p>\r\n<p>In the plugin settings, enter the Access key:&nbsp;Console -&gt; Settings -&gt; CleanTalk -&gt; Access Key</p>\r\n<p><img src="../images/screens/en/wp_setup_key.png" alt="" width="1024" height="906" /></p>\r\n<p>Also, you can switch the plugin to use Global Access key. In this way the plugin doesn''t show any options to enter Access key in plugin settings and doesn''t show Trial banner in WordPress backend. To setup global CleanTalk access key for all websites in WPMU, define constant in your wp-config.php file before defining database constants:</p>\r\n<p>beginphp</p>\r\n<p>define(''CLEANTALK_ACCESS_KEY'', ''place your key here'');</p>\r\n<p>endphp</p>\r\n<p>Now, all subsites will have this access key.</p>', '', '', '2017-10-25 11:42:21', NULL, NULL),
(435, 533, 'help', 'How to protect osTicket from spambots', '<h1>How to protect osTicket from spambots</h1>\n<p>&nbsp;</p>\n<ol>\n<li>\n<h4>Download&nbsp;<a href="https://github.com/CleanTalk/php-antispam">CleanTalk API</a> from Github.com&nbsp;</h4>\n</li>\n<li>\n<h4>Unzip the downloaded archive to your root directory. You will see the folder with the name "php-antispam-master". Rename it to "cleantalk"</h4>\n</li>\n<li>\n<h4>Edit "open.php" script in your osTicket folder</h4>\n</li>\n</ol>\n<p>In order to start using the CleanTalk Anti-Spam API you need to connect it to your site. You can paste this code at the top of your script:</p>\n<p>beginphp</p>\n<p>session_start(); //Session<br />require_once (dirname(__FILE__) . ''/cleantalk/cleantalk.class.php'');</p>\n<p>endphp</p>\n<p>Replace the following code:</p>\n<p>beginphp</p>\n<p>if(($ticket=Ticket::create($vars, $errors, SOURCE))){<br />$msg=__(''Support ticket request created'');<br />// Drop session-backed form data<br />unset($_SESSION['':form-data'']);<br />//Logged in...simply view the newly created ticket.<br />if($thisclient &amp;&amp; $thisclient-&gt;isValid()) {<br />session_write_close();<br />session_regenerate_id();<br />@header(''Location: tickets.php?id=''.$ticket-&gt;getId());<br />}<br />}else{<br />$errors[''err'']=$errors[''err'']?$errors[''err'']:__(''Unable to create a ticket. Please correct errors below and try again!'');<br />}</p>\n<p>endphp</p>\n<p>With this one:</p>\n<p>beginphp</p>\n<p>$uform = UserForm::getUserForm()-&gt;getForm($_POST);<br />$config_url = ''http://moderate.cleantalk.org''; //Server address<br />$auth_key = ''000000000''; //&nbsp;Your access key<br />$ct_request = new CleantalkRequest();<br />$ct_request-&gt;auth_key = $auth_key; //access key variable<br />$ct_request-&gt;message = $tform-&gt;getField(''message''); //message to check<br />$ct_request-&gt;method_name = ''check_message''; //method&nbsp;<br />$ct_request-&gt;sender_email = $uform-&gt;getClean()[''email'']; //e-mail to check<br />$ct_request-&gt;sender_nickname = $uform-&gt;getClean()[''name'']; //name to check&nbsp;<br />$ct_request-&gt;agent = ''php-api''; ///extension name<br />$ct_request-&gt;sender_ip = $_SERVER["REMOTE_ADDR"]; //ip to check<br />$ct = new Cleantalk();<br />$ct-&gt;server_url = $config_url;<br />$ct_result = $ct-&gt;isAllowMessage($ct_request);<br />if ($ct_result-&gt;allow == 1) {<br />if(($ticket=Ticket::create($vars, $errors, SOURCE))){<br />$msg=__(''Support ticket request created'');<br />// Drop session-backed form data<br />unset($_SESSION['':form-data'']);<br />//Logged in...simply view the newly created ticket.<br />if($thisclient &amp;&amp; $thisclient-&gt;isValid()) {<br />session_write_close();<br />session_regenerate_id();<br />@header(''Location: tickets.php?id=''.$ticket-&gt;getId());<br />}<br />}else{<br />$errors[''err'']=$errors[''err'']?$errors[''err'']:__(''Unable to create a ticket. Please correct errors below and try again!'');<br />}<br />} else {<br />echo ''Comment blocked. Reason '' . $ct_result-&gt;comment;<br />}</p>\n<p>endphp</p>\n<p>The following line can be replaced with your custom warning page:</p>\n<p>beginphp</p>\n<p>echo ''Comment blocked. Reason '' . $ct_result-&gt;comment;</p>\n<p>endphp</p>\n<p>For example:</p>\n<p>beginphp</p>\n<p>@header(''Location: blocked.php'');</p>\n<p>endphp</p>\n<p style="text-align: justify;">&nbsp;</p>\n<h3 style="text-align: justify;">Use the blacklisted e-mail s@cleantalk.org to create a ticket. As a result, you will see the blocking message at the top of the page</h3>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>', 'osTicket spam protection, api, php, osticket', 'How to protect osTicket from spambots', '2017-10-25 11:42:21', NULL, NULL),
(179, 275, 'help', 'API Method send_feedback - returns moderation results', '<h1>API Method send_feedback</h1>\n<p>This method should be used only for moderator feedbacks. It doesn''t check spam. It sends back result of manual moderation. For other purposes use you could use other methods:</p>\n<ul>\n<li><a href="/help/api-check-message">check_message</a>&nbsp;- Post/commentary check.</li>\n<li><a href="/help/api-check-newuser">check_newuser</a>&nbsp;- Registrations check.</li>\n<li><a href="/api-spam-check">spam_check</a> - mass check for IPs, emails for spam activity.</li>\n<li><a href="/help/api-backlinks-check">backlinks_check</a> - mass check for backlinks in spam comments.</li>\n</ul>\n<h2>Required parameters:</h2>\n<ul>\n<li>method_name - should be ''send_feedback'';</li>\n<li>auth_key - API key. To obtain a key please get an account here https://cleantalk.org/register?platform=api;</li>\n<li>feedback - string like "&lt;request_id1&gt;:&lt;0|1&gt;;&lt;request_id2&gt;:&lt;0|1&gt;;&lt;request_id3&gt;:&lt;0|1&gt;;" where &lt;0|1&gt; is moderator decision&nbsp;about appropriate request_id</li>\n</ul>\n<h2>Example:</h2>\n<h3>Wget&nbsp;request:</h3>\n<p>beginplain</p>\n<p>wget -O- --post-data=''{"method_name":"send_feedback","auth_key":"your_acccess_key","feedback":"4e8bc562bdaed613107d8b8695:0;4bd0105024bbaf60c57176b766e:1"}'' https://moderate.cleantalk.org/api2.0</p>\n<p>endplain</p>\n<h2>Response:</h2>\n<p>Server''s&nbsp;response doesn''t depend on the platform.</p>\n<p>beginplain</p>\n<p>{<br />"received&nbsp;" : 2,<br />"comment" : "OK"<br />}</p>\n<p>endplain</p>\n<h3>Explanation:</h3>\n<ul>\n<li>received - number of received request IDs;</li>\n<li>comment - server answer, normally ''Ok'';</li>\n</ul>\n<h2>&nbsp;</h2>', 'send_feedback, cleantalk, api', 'API Method send_feedback - returns moderation results', '2017-10-25 11:42:21', NULL, NULL),
(9, 113, 'help', 'API Method check_newuser - Registrations Check', '<h1>API Method check_newuser</h1>\n<p>This method should be used only for registration checking. For other purposes use you could use other methods:</p>\n<ul>\n<li><a href="/help/api-check-message">check_message</a>&nbsp;- API spam filter to check Posts/comments.</li>\n<li><a href="/api-send-feedback">send_feedback</a> - Send feedback to CleanTalk.</li>\n<li><a href="/api-spam-check">spam_check</a> - mass check for IPs, emails for spam activity.</li>\n<li><a href="/help/api-backlinks-check">backlinks_check</a> - mass check for backlinks in spam comments.</li>\n</ul>\n<h2>Required parameters:</h2>\n<ul>\n<li>method_name - method nam, must be ''check_message'';</li>\n<li>auth_key - API key. To obtain a key please get an account here <a href="/register?platform=api;">https://cleantalk.org/register?platform=api;</a></li>\n<li>sender_email - email for spam checking;</li>\n<li>sender_ip - IP for spam checking;</li>\n<li>js_on - is JavaScript enabled in user''s browser;</li>\n<li>submit_time - form submitting time in seconds;</li>\n</ul>\n<h4>There is the list of the necessary parametrs which hardly affect on filtration.<br />Most of the requests will be marked as spam without them:</h4>\n<ul>\n<li>all_headers - HTTP-request headers (JSON encoded);</li>\n<li>sender_nickname - sender nickname for spam checking;</li>\n<li>sender_info - information about sender, should be JSON encoded, next fields are mandatory:</li>\n<ul>\n<li>REFFERRER - content of $_SERVER[''HTTP_REFERER'']</li>\n<li>USER_AGENT - content of $_SERVER[''HTTP_USER_AGENT'']</li>\n</ul>\n</ul>\n<h2>Additional parameters:</h2>\n<p>Sending this parameters will improve filtration.</p>\n<ul>\n<li>sender_info - any additional information about sender, should be JSON encoded, see example;</li>\n<li>response_lang - API response language;</li>\n<li>tz - sender''s timezone;</li>\n<li>phone - sender''s phone nubmer;</li>\n</ul>\n<h2>Examples:</h2>\n<h3>Using Wget:</h3>\n<p>beginplain</p>\n<p>wget -O- --post-data=''{"method_name":"check_newuser","auth_key":"your_acccess_key","sender_email":"stop_email@example.com","sender_nickname":"John Doe","sender_ip":"127.0.0.1","js_on":1,"submit_time":15}'' https://moderate.cleantalk.org/api2.0</p>\n<p>endplain</p>\n<h3>Using PHP:&nbsp;</h3>\n<p>beginphp</p>\n<p>&lt;?php<br />session_start();<br />require_once (dirname(__FILE__) . ''/cleantalk.class.php'');<br />// Take params from config<br />$config_url = ''http://moderate.cleantalk.org/api2.0/'';<br />$auth_key = ''enter key''; // Set Cleantalk auth key<br />if (count($_POST)) {<br />$sender_nickname = ''John Dow'';<br />if (isset($_POST[''login'']) &amp;&amp; $_POST[''login''] != '''')<br />$sender_nickname = $_POST[''login''];<br />$sender_email = ''stop_email@example.com'';<br />if (isset($_POST[''email'']) &amp;&amp; $_POST[''email''] != '''')<br />$sender_email = $_POST[''email''];<br />$sender_ip = null;<br />if (isset($_SERVER[''REMOTE_ADDR'']))<br />$sender_ip = $_SERVER[''REMOTE_ADDR''];<br />$js_on = 0; <br />if (isset($_POST[''js_on'']) &amp;&amp; $_POST[''js_on''] == date("Y"))<br />$js_on = 1;<br />// The facility in which to store the query parameters<br />$ct_request = new CleantalkRequest();<br />$ct_request-&gt;auth_key = $auth_key;<br />$ct_request-&gt;agent = ''php-api'';<br />$ct_request-&gt;sender_email = $sender_email; <br />$ct_request-&gt;sender_ip = $sender_ip; <br />$ct_request-&gt;sender_nickname = $sender_nickname; <br />$ct_request-&gt;js_on = $js_on;<br />$ct_request-&gt;submit_time = time() - (int) $_SESSION[''ct_submit_time''];<br />//Additional parameters. You could the description at the bottom of the page. <br />$ct = new Cleantalk();<br />$ct-&gt;server_url = $config_url;<br />// Check<br />$ct_result = $ct-&gt;isAllowUser($ct_request);<br />if ($ct_result-&gt;allow == 1) {<br />echo ''User allowed. Reason '' . $ct_result-&gt;comment;<br />} else {<br />echo ''User forbidden. Reason '' . $ct_result-&gt;comment;<br />}<br />echo ''&lt;br /&gt;&lt;br /&gt;'';<br />}<br />else<br />{<br />$_SESSION[''ct_submit_time''] = time();<br />}<br />?&gt;</p>\n<p>endphp</p>\n<h3>HTML code:</h3>\n<p>beginphp</p>\n<p><br />&lt;form method="post"&gt;<br />&lt;label for="login"&gt;Login:&lt;label&gt;<br />&lt;input type="text" name="login" id="login" /&gt;<br />&lt;br /&gt;<br />&lt;label for="email"&gt;Email:&lt;label&gt;<br />&lt;input type="text" name="email" id="email" value="" /&gt;<br />&lt;br /&gt;<br />&lt;input type="hidden" name="js_on" id="js_on" value="0" /&gt;<br />&lt;input type="submit" /&gt;<br />&lt;/form&gt;<br />&lt;script type="text/javascript"&gt;<br />var date = new Date();<br />document.getElementById("js_on").value = date.getFullYear(); <br />&lt;/script&gt;</p>\n<p>endphp</p>\n<p><strong>Submit_time</strong> and <strong>js_on</strong> parameters are very important for checking. <strong>Submit_time</strong> is the difference between submitting form time and page accessing time. <strong>Js_on</strong> can be calculated by evaluating some JavaScript code in browser and comparing with reference value on server side.</p>\n<h3>Example of additional parameters using:</h3>\n<p>beginphp</p>\n<p>$sender_info = array(<br />''page_url'' =&gt; htmlspecialchars(@$_SERVER[''SERVER_NAME''].@$_SERVER[''REQUEST_URI'']),<br />''REFFERRER'' =&gt; htmlspecialchars(@$_SERVER[''HTTP_REFERER'']),<br />''USER_AGENT'' =&gt; htmlspecialchars(@$_SERVER[''HTTP_USER_AGENT'']),<br />''fields_number'' =&gt; sizeof($_POST),<br />);&nbsp;<br />$sender_info = json_encode($sender_info);<br />if ($sender_info === false)<br />$sender_info = '''';&nbsp;<br />$ct_request-&gt;sender_info = $sender_info;&nbsp;<br /><br />$ct_request-&gt;phone = ''+1 123-456-78-90'';&nbsp;<br /><br />$ct_request-&gt;all_headers = json_encode(apache_request_headers());</p>\n<p>endphp</p>\n<p>More info can be found on our GitHub: [&nbsp;<a title="CleanTalk GitHub" href="https://github.com/CleanTalk/php-antispam" target="_blank">https://github.com/CleanTalk/php-antispam</a> ].</p>\n<h2>Response:</h2>\n<p>Server''s&nbsp;response doesn''t depend on the platform.</p>\n<p>beginplain</p>\n<p>{<br />"version" : "7.47",<br />"inactive" : 0,<br />"js_disabled" : 0,<br />"blacklisted" : 1,<br />"comment" : "*** Forbidden. Sender blacklisted. ***",<br />"codes" : "FORBIDDEN BL",<br />"fast_submit" : 0,<br />"id" : "5a49267e202169b3a4d9ddefee190065",<br />"account_status" : 1,<br />"allow" : 0<br />}</p>\n<p>endplain</p>\n<h3>Explanation:</h3>\n<ul>\n<li>js_disabled - JavaScript is disabled;</li>\n<li>blacklisted - sender is in CleanTalk''s blacklists;</li>\n<li>comment - comment for server''s decision or for another errors (wrong access key etc.);</li>\n<li>codes - answer codes.</li>\n<li>fast_submit - to fast form submitting;</li>\n<li>id - message ID (helpful for our support),</li>\n<li>account_status - is account enabled or not (0 or 1);</li>\n<li>allow - is message allowed (1) or not (0);</li>\n</ul>\n<h3>Available features:</h3>\n<ul>\n<li><a href="https://cleantalk.org/help/blacklist-usage" target="_blank">CleanTalk''s Personal Blacklist or Whitelist</a> feature helps you to block unwanted users and to allow users that were recognized as spammers.</li>\n<li><a href="https://cleantalk.org/help/stop-word" target="_blank">Stop-Word</a> feature allows you to block comments and nicknames which contain any word from your Stop-Word List.</li>\n<li><a href="https://cleantalk.org/extra-package#country_blacklist" target="_blank">Country-Blacklist</a> allows you to block all comments/registration to users from selected countries.</li>\n</ul>\n<p><strong>Notice:</strong> Stop-Word and Country-Blackist features are availabe after purchaising our <a href="https://cleantalk.org/extra-package " target="_blank">Extra Package</a>. Go to your <a href="https://cleantalk.org/my/bill/recharge" target="_blank">Renewal Licence Page</a> to see the details.</p>\n<p>&nbsp;</p>\n<p>You could see the codes explanation at the bottom of this article: <a href="/help/api-without">https://cleantalk.org/help/api-without</a>.</p>', 'check_newuser, api, php', 'API Spam Protection Method check_newuser - Registrations Check', '2017-10-25 11:42:21', NULL, NULL),
(163, 259, 'help', 'Compatibility with AMP', '<h1>Compatibility with AMP</h1>\r\n<p>CleanTalk is fully compatible with AMP start from CT&nbsp;version 5.39:</p>\r\n<ol>\r\n<li>If you want to use them together, please upgrade plugin.</li>\r\n<li>To forbid CleanTalk to add JavaScript code on AMP pages turn off the option ''Use AJAX for JavaScript check'' in Advanced settings</li>\r\n</ol>\r\n<p>These steps will provide fully compatibility CleanTalk with AMP (Accelerated Mobile Pages).</p>\r\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/wordpress/wp+settings+AJAX+-+NO.png" alt="CleanTalk use AJAX - NO" width="1000" height="695" /></p>\r\n<p>&nbsp;</p>\r\n<p><strong>If you still have questions you can always ask our Tech support by creating a ticket <a href="/my/support">https://cleantalk.org/my/support</a>.</strong></p>', 'amp, compatibility, Accelerated Mobile Pages', 'Cleantalk Compatibility with AMP', '2017-10-25 11:42:21', NULL, NULL),
(21, 123, 'help', 'Installing CleanTalk on WordPress Network (WPMU)', '<h1>Installing CleanTalk on WordPress Network (WPMU)</h1>\r\n<p>1. Firstly, be sure you configured network according official WordPress manual: <a href="https://codex.wordpress.org/Create_A_Network">https://codex.wordpress.org/Create_A_Network</a></p>\r\n<p>2.&nbsp;Install CleanTalk: In top left menu select "My sites" -&gt; "Network admin" -&gt; "Plugins" -&gt; "Add new" -&gt; type ''cleantalk''</p>\r\n<p><img src="../images/screens/en/wp_find_cleantalk.png" alt="" width="1024" height="548" /></p>\r\n<p>In the plugin settings, enter the Access key:&nbsp;Console -&gt; Settings -&gt; CleanTalk -&gt; Access Key</p>\r\n<p><img src="../images/screens/en/wp_setup_key.png" alt="" width="1024" height="906" /></p>\r\n<p>Also, you can switch the plugin to use Global Access key. In this way the plugin doesn''t show any options to enter Access key in plugin settings and doesn''t show Trial banner in WordPress backend. To setup global CleanTalk access key for all websites in WPMU, define constant in your wp-config.php file before defining database constants:</p>\r\n<p>beginphp</p>\r\n<p>define(''CLEANTALK_ACCESS_KEY'', ''place your key here'');</p>\r\n<p>endphp</p>\r\n<p>&nbsp;</p>\r\n<p>Now, all subsites will have this access key.</p>', 'wpmu, multisite, wordpress, cleantalk, install', 'CleanTalk on WordPress Network (WPMU)', '2017-10-25 11:42:21', NULL, NULL);
INSERT INTO `articles` (`article_id`, `article_linkid`, `article_where`, `article_title`, `article_content`, `keywords`, `description`, `updated`, `widget`, `widget_url`) VALUES
(10, 114, 'help', 'Spam Protection API Method check_message - Checks Messages for Spam Activity', '<h1>API spam filter "check_message"</h1>\n<p>&nbsp;</p>\n<h2>Required parameters:</h2>\n<ul>\n<li>method_name - method nam, must be ''check_message'';</li>\n<li>auth_key - API key. To obtain a key please get an account here <a href="/register?platform=api">https://cleantalk.org/register?platform=api</a>;</li>\n<li>sender_email - email for spam checking;</li>\n<li>sender_ip - IP for spam checking;</li>\n<li>js_on - is JavaScript enabled in user''s browser;</li>\n<li>submit_time - form submitting time in seconds;</li>\n</ul>\n<h4>There is the list of the necessary parametrs which hardly affect on filtration. Most of the requests will be marked as spam without them:</h4>\n<ul>\n<li>all_headers - HTTP-request headers (JSON encoded);</li>\n<li>sender_nickname - sender nickname for spam checking;</li>\n<li>message - text message for checking, can contain HTML-tags;</li>\n<li>sender_info - information about sender, should be JSON encoded, next fields are mandatory:</li>\n<ul>\n<li>REFFERRER - content of $_SERVER[''HTTP_REFERER'']</li>\n<li>USER_AGENT - content of $_SERVER[''HTTP_USER_AGENT'']</li>\n</ul>\n</ul>\n<p>&nbsp;</p>\n<h2>Additional&nbsp;parameters:</h2>\n<p>Sending this parameters will improve filtration.</p>\n<ul>\n<li>sender_info - any additional information about sender, should be JSON encoded.</li>\n<li>response_lang - API&nbsp;response language;</li>\n<li>post_info - additional information about message (JSON encoded, must contain key named ''comment_type'');</li>\n<li>stoplist_check - logical flag to check message via stop-words list (1 or&nbsp;0) (should be enabled in account);</li>\n</ul>\n<h2>Examples:</h2>\n<h3>Using Wget:</h3>\n<p>beginplain</p>\n<p>wget -O- --post-data=''{"method_name":"check_newuser","auth_key":"your_acccess_key","sender_email":"stop_email@example.com","sender_nickname":"John Doe","sender_ip":"127.0.0.1","js_on":1,"submit_time":15}'' https://moderate.cleantalk.org/api2.0</p>\n<p>endplain</p>\n<h3>Using PHP:&nbsp;</h3>\n<p>beginphp</p>\n<p>&lt;?php<br />session_start();<br />require_once (dirname(__FILE__) . ''/cleantalk.class.php'');<br />// Take params from config<br />$config_url = ''http://moderate.cleantalk.org/api2.0/'';<br />$auth_key = ''enter key''; // Set Cleantalk auth key<br />if (count($_POST)) {<br />$sender_nickname = ''John Dow'';<br />if (isset($_POST[''login'']) &amp;&amp; $_POST[''login''] != '''')<br />$sender_nickname = $_POST[''login''];<br />$sender_email = ''stop_email@example.com'';<br />if (isset($_POST[''email'']) &amp;&amp; $_POST[''email''] != '''')<br />$sender_email = $_POST[''email''];<br />$sender_ip = null;<br />if (isset($_SERVER[''REMOTE_ADDR'']))<br />$sender_ip = $_SERVER[''REMOTE_ADDR''];<br />$js_on = 0; <br />if (isset($_POST[''js_on'']) &amp;&amp; $_POST[''js_on''] == date("Y"))<br />$js_on = 1;<br />$message = null; <br />if (isset($_POST[''message'']) &amp;&amp; $_POST[''message''] != '''')<br />$message = $_POST[''message''];<br />// The facility in which to store the query parameters<br />$ct_request = new CleantalkRequest();<br />$ct_request-&gt;auth_key = $auth_key;<br />$ct_request-&gt;agent = ''php-api'';<br />$ct_request-&gt;sender_email = $sender_email; <br />$ct_request-&gt;sender_ip = $sender_ip; <br />$ct_request-&gt;sender_nickname = $sender_nickname; <br />$ct_request-&gt;js_on = $js_on;<br />$ct_request-&gt;message = $message;<br />$ct_request-&gt;submit_time = time() - (int) $_SESSION[''ct_submit_time''];<br />//Additional parameters. You could the description at the bottom of the page.<br />$ct = new Cleantalk();<br />$ct-&gt;server_url = $config_url;<br />// Check<br />$ct_result = $ct-&gt;isAllowMessage($ct_request);<br />if ($ct_result-&gt;allow == 1) {<br />echo ''Message allowed. Reason '' . $ct_result-&gt;comment;<br />} else {<br />echo ''Message forbidden. Reason '' . $ct_result-&gt;comment;<br />}<br />echo ''&lt;br /&gt;&lt;br /&gt;'';<br />}<br />else<br />{<br />$_SESSION[''ct_submit_time''] = time();<br />}<br />?&gt;</p>\n<p>endphp</p>\n<h3>HTML code:</h3>\n<p>beginphp</p>\n<p>&lt;form method="post"&gt;<br />&lt;label for="login"&gt;Login:&lt;label&gt;<br />&lt;input type="text" name="login" id="login" /&gt;<br />&lt;br /&gt;<br />&lt;label for="email"&gt;Email:&lt;label&gt;<br />&lt;input type="text" name="email" id="email" value="" /&gt;<br />&lt;br /&gt;<br />&lt;label for="message"&gt;Message:&lt;label&gt;<br />&lt;textarea name="message" id="message"&gt;&lt;/textarea&gt;<br />&lt;br /&gt;<br />&lt;input type="hidden" name="js_on" id="js_on" value="0" /&gt;<br />&lt;input type="submit" /&gt;<br />&lt;/form&gt;<br />&lt;script type="text/javascript"&gt;<br />var date = new Date();<br />document.getElementById("js_on").value = date.getFullYear(); <br />&lt;/script&gt;</p>\n<p>endphp</p>\n<p><strong>Submit_time</strong> and <strong>js_on</strong> parameters are very important for checking. <strong>Submit_time</strong> is the difference between submitting form time and page accessing time. j<strong>s_on</strong> can be calculated by evaluating some JavaScript code in browser and comparing with reference value on server side. See in example, how you can calculate them:&nbsp;<a href="/help/api-without">https://cleantalk.org/help/api-without</a></p>\n<h3>Example&nbsp;of additional parameters&nbsp;using (PHP):</h3>\n<p>beginphp</p>\n<p>$sender_info = array(<br />''page_url'' =&gt; htmlspecialchars(@$_SERVER[''SERVER_NAME''].@$_SERVER[''REQUEST_URI'']),<br />''REFFERRER'' =&gt; htmlspecialchars(@$_SERVER[''HTTP_REFERER'']),<br />''USER_AGENT'' =&gt; htmlspecialchars(@$_SERVER[''HTTP_USER_AGENT'']),<br />''fields_number'' =&gt; sizeof($_POST),<br />);&nbsp;<br />$sender_info = json_encode($sender_info);<br />if ($sender_info === false)<br />$sender_info = '''';&nbsp;<br />$ct_request-&gt;sender_info = $sender_info;&nbsp;<br /><br />$ct_request-&gt;stoplist_check = 1;&nbsp;<br /><br />$ct_request-&gt;all_headers = json_encode(apache_request_headers());<br /><br />$post_info[''comment_type''] = ''general_comment'';&nbsp;<br />$post_info = json_encode($post_info);<br />if ($post_info === false)<br />$post_info = '''';&nbsp;<br />$ct_request-&gt;post_info = $post_info;</p>\n<p>endphp</p>\n<p>More info can be found on our GitHub: [&nbsp;<a title="CleanTalk GitHub" href="https://github.com/CleanTalk/php-antispam" target="_blank">https://github.com/CleanTalk/php-antispam</a> ].</p>\n<h2>Response:</h2>\n<p>Server''s&nbsp;response doesn''t depend on the platform.</p>\n<p>beginplain</p>\n<p>{<br />"stop_queue" : 0,<br />"inactive" : 0,<br />"version" : "7.47",<br />"spam" : 1,<br />"js_disabled" : 0,<br />"comment" : "*** Forbidden. Sender blacklisted. ***",<br />"codes" : "FORBIDDEN BL",<br />"blacklisted" : 1,<br />"fast_submit" : 0,<br />"account_status" : "1",<br />"id" : "433289e278ae059f8fc58914fc890de2",<br />"allow" : 0<br />}</p>\n<p>endplain</p>\n<h3>Explanation:</h3>\n<ul>\n<li>stop_queue - stop queue for comment approvement (comment&nbsp;100% is a spam);</li>\n<li>spam - comment - spam, possible&nbsp;to send it to hand moderation is&nbsp;flag&nbsp;stop_queue == 0;</li>\n<li>js_disabled - JavaScript is disabled;</li>\n<li>comment - comment for server''s decision or for another errors (wrong access key etc.);</li>\n<li>codes - answer codes.</li>\n<li>blacklisted - sender is in CleanTalk''s blacklists;</li>\n<li>fast_submit - to fast form submitting;</li>\n<li>account_status - is account enabled or not (0 or 1);</li>\n<li>id - message ID (helpful for our support),</li>\n<li>allow - is message allowed (1) or not (0);</li>\n</ul>\n<p>You could see the codes explanation at the bottom of this article: <a href="/help/api-without">https://cleantalk.org/help/api-without</a>.</p>\n<h3>Available features:</h3>\n<ul>\n<li><a href="https://cleantalk.org/help/blacklist-usage" target="_blank">CleanTalk''s Personal Blacklist or Whitelist</a> feature helps you to block unwanted users and to allow users that were recognized as spammers.</li>\n<li><a href="https://cleantalk.org/help/stop-word" target="_blank">Stop-Word</a> feature allows you to block comments and nicknames which contain any word from your Stop-Word List.</li>\n<li><a href="https://cleantalk.org/extra-package#country_blacklist" target="_blank">Country-Blacklist</a>&nbsp; allows you to block all comments/registration to users from selected countries.</li>\n</ul>\n<p><strong>Notice:&nbsp;</strong>Stop-Word and Country-Blackist features are availabe after purchaising our <a href="https://cleantalk.org/extra-package " target="_blank">Extra Package</a>. Go to your <a href="https://cleantalk.org/my/bill/recharge" target="_blank">Renewal Licence Page</a> to see the details.</p>\n<p>&nbsp;</p>\n<p><strong>This spam protection API should be used only for registration checking. For other purposes use you could use other methods:</strong></p>\n<ul style="list-style-type: disc;">\n<li><a href="/help/api-check-newuser">check_newuser</a> - API spam filter for Registrations.</li>\n<li><a href="/api-send-feedback">send_feedback</a> - Send feedback to CleanTalk.</li>\n<li><a href="/api-spam-check">spam_check</a> - mass check for IPs, emails for spam activity.</li>\n<li><a href="/help/api-backlinks-check">backlinks_check</a> - mass check for backlinks in spam comments.</li>\n</ul>', 'API spam filter, spam protection api, api, check_message, cleantalk', 'Spam Protection API check_message - Checks Messages/Comments for Spam', '2017-10-25 11:42:21', NULL, NULL),
(11, 115, 'help', 'Blocked Messages with Reason: Forbidden. Please Enable JavaScript. Spam Sender Name.', '<h1>I See in&nbsp;Logs a Lot of Blocked Messages with Reason: "Forbidden. Please Enable JavaScript. Spam Sender Name."</h1>\n<p>&nbsp;</p>\n<p>A lot of spam bots can''t perform JavaScript code, so it is one of the important checks and most of the spam bots will be blocked with reason "Forbidden. Please enable JavaScript. Spam sender name." All browsers can perform JS code, so real visitors won''t be blocked.</p>\n<p>&nbsp;</p>\n<p>If you think that message was blocked wrong, let us know about it via:</p>\n<ul style="list-style-type: square;">\n<li>E-mail&nbsp;[ <a href="mailto:support@cleantalk.org">support@cleantalk.org</a> ].</li>\n<li>Request&nbsp;[ <a href="/my/support">https://cleantalk.org/my/support</a> ].</li>\n<li>Forum&nbsp;[ <a href="https://community.cleantalk.org/">https://community.cleantalk.org/</a> ].</li>\n</ul>\n<p>&nbsp;</p>\n<p>If you or someone of&nbsp;your visitors have error "Forbidden. Enable JavaScript." please check JavaScript support in your browser and do JavaScript test at this page:&nbsp;<a href="http://cleantalk.org/checkout-javascript-support">Check JavaScript support</a>.</p>\n<p>&nbsp;</p>\n<p><a href="http://enable-javascript.com/">How to enable JavaScript in a web browser?</a></p>\n<p>&nbsp;</p>', 'blocked message, log, CleanTalk, help, JavaScript, please enable JavaScript', 'Blocked Messages with Reason: Forbidden. Please Enable JavaScript. Spam Sender Name.', '2017-10-25 11:42:21', NULL, NULL),
(12, 116, 'help', ' I Don''t Want to Store the Content of Messages in the Logs', '<h1>I Don''t Want to Store the Content of Messages in the Logs</h1>\n<p>&nbsp;</p>\n<p>You can disable storing of all information in the log except the IP.</p>\n<p><a href="/my/profile">Go to Control Panel &mdash;&gt; Profile</a>, tick&nbsp;the option "Don''t save approved requests" and press the button&nbsp;"Save".</p>\n<p>Now only date/time and IP will be kept in the logs.</p>\n<p>&nbsp;</p>', 'content, message, log', 'Storing the Content of Messages in the Logs', '2017-10-25 11:42:21', NULL, NULL),
(13, 117, 'help', 'Presskit', '<h1 style="text-align: center;"><br />CleanTalk Cloud Anti-Spam for Websites</h1>\n<p style="text-align: center;"><br />No Captcha, no questions, no counting animals, no puzzles, no math. Fight spam!</p>\n<p style="text-align: center;">Anti-Spam Apps: WordPress, Joomla, Drupal, phpBB, Magento, IP.Board, SMF, TYPO3, XenForo, MediaWiki, vBulletin, PHP, Perl, Python, .NET, Yii, Ruby.</p>\n<p style="text-align: center;">&nbsp; &nbsp; &nbsp; Mobile Apps&nbsp;<a class="integration__item__link" href="https://itunes.apple.com/us/app/cleantalk/id825479913?mt=8"><img class="reset" src="../images/ios.png" alt="iPhone/iPad" width="24" height="24" /><span class="integration__item__text">iPhone/iPad</span></a><a class="integration__item__link" href="https://play.google.com/store/apps/details?id=org.cleantalk.app"><img class="reset" src="../images/android.png" alt="Android" width="24" height="24" /><span class="integration__item__text">Android</span></a></p>\n<h2 style="text-align: center;"><strong>Description</strong></h2>\n<p>The main function of the CleanTalk to provide the owners and administrators of websites reliable tool to protect the web site against spam and spam bots. Invisible to the visitors of the web site, simple and convenient for the administrator, CleanTalk automatically blocks 99,998% of the spam. With its unique capabilities, CleanTalk surpasses analogues by accurate of spam detection, provides a report on the work for the administrator, the option to check existing comments/users on the spam and speed.</p>\n<p>CleanTalk uses protection methods which are invisible to site visitors. Connecting to the service eliminates needs for CAPTCHA, questions and answers, and other methods of protection that complicate the exchange of information on the site.</p>\n<p>&nbsp;</p>\n<h2 style="text-align: center;"><strong>How does it work?</strong></h2>\n<ol>\n<li>A visitor writes a comment or registers.</li>\n<li>CleanTalk plugin sends action parameters into the CleanTalk cloud</li>\n<li>Service analyzes the parameters.</li>\n<li>If this is a visitor, the comment will be published. If it&rsquo;s a spam bot, then CleanTalk blocks this comment or registering.</li>\n<li>Parameters are written to the log which can be viewed in the Control Panel service.</li>\n</ol>\n<p><img style="display: block; margin-left: auto; margin-right: auto;" src="../images/presskit/pk1.jpg" alt="" width="755" height="250" /></p>\n<p>&nbsp;</p>\n<h2 style="text-align: center;"><strong>What makes us different from other spam protection services? Why choose us?</strong></h2>\n<ol>\n<li>\n<p>No Captcha or other actions required from the user. Captchas lower conversion rates, irritate the customer and makes them leave your site. They are unsafe and let a lot of spam in. This transfers the spam problem from the webmaster to the customer, which means that you still have spam problems but you don&rsquo;t know what to do about them. You don&rsquo;t know how many customers you have lost because they couldn&rsquo;t insert the symbols correctly.</p>\n</li>\n<li>\n<p>It is more reliable than similar systems. Other systems let spam in; they have different operating principles and can&rsquo;t be as reliable as&nbsp;CleanTalk. CleanTalk algorithms estimate user behavior and the way forms are filled in. These multilevel checks enable CleanTalk to reach high efficiency in spam protection. With other protection systems, real users might be mistaken for spam; all spam messages are put in a spam folder and you have to take time to look through them all to not miss a client. This folder has to be cleaned all the time, which leads to more load on the website, causing it to start working slower.</p>\n</li>\n<li>\n<p>CleanTalk is more reliable than protection based on JavaScript check. Spam bots can run JavaScript and their number grows, meaning that spam grows too. Some customers turn off the JavaScript support in their browsers for safety purposes and these customers leave your site without you even knowing about it.</p>\n</li>\n<li>\n<p>Clear statistics. CleanTalk delivers extremely open statistics for all incoming registrations/ comments, which gives you full control of the process. You will never miss a message. In order to inform clients, CleanTalk sends a weekly report to your email containing information on the number of approved and spam registrations/comments. CleanTalk provides you with diagrams for every website so you can see the dynamics of spam activity or legitimate comments/registrations on your website.</p>\n</li>\n<li>\n<p>CleanTalk protects all forms at once &ndash; comments, registration, feedback, contact, reviews. There is no need to install a separate plugin for a specific form.</p>\n</li>\n<li>\n<p>Easy to install and use. Even my grandma can do it.</p>\n</li>\n<li>\n<p>CleanTalk operates automatically, which saves you time and resources, letting you focus on developing and improving your website and business without being distracted by other matters.</p>\n</li>\n<li>\n<p>CleanTalk is a premium service. This guarantees that the plugin won&rsquo;t disappear the way others do. You can always contact technical support and your request will be processed promptly. We provide a high quality service and therefore do not distribute a free version. Free versions always have limitations which influences the quality of spam protection. Purchasing a year of service gives you savings &ndash; you get:</p>\n</li>\n</ol>\n<ul>\n<li>\n<div>100% protection against spam bots</div>\n</li>\n<li>\n<div>Time and resources saving</div>\n</li>\n<li>\n<div>More registrations/comments/visitors</div>\n</li>\n<li>\n<div>Protect several websites at once at different CMS</div>\n</li>\n<li>\n<div>Easy to install and use</div>\n</li>\n<li>\n<div>Traffic acquisition and user loyalty</div>\n</li>\n<li>\n<div>24/7 technical support</div>\n</li>\n<li>\n<div>Clear statistics</div>\n</li>\n<li>\n<div>No captcha, puzzles, etc.</div>\n</li>\n<li>\n<div>Free mobile app</div>\n</li>\n</ul>\n<p>Everyday we save you time so you can do something good for yourself, like to have a cup of coffee, read the news or buy donuts.</p>\n<p>&nbsp;</p>\n<h2 style="text-align: center;"><strong>Nowadays CleanTalk is generally recognized by users who say CleanTalk is one of the best anti-spam services.</strong></h2>\n<p><img style="display: block; margin-left: auto; margin-right: auto;" src="../images/presskit/pk2.png" alt="" width="869" height="1021" /></p>\n<h2 style="text-align: center;"><strong>Features</strong></h2>\n<p>CleanTalk one of the fastest plug-in that allows you to lower the server load. One of the important parameters for each Web master is the speed of the site, so we make sure that our plug-in to consume as little as possible of server resources. Cloud service provides the advantage that all the checks take place in the cloud.</p>\n<p>The CleanTalk company developed unique algorithms to assess the behavior of the visitor. CleanTalk analyzes user behavior and parameter of the filled out form. Anti spam module, installed on the website, sends the behaviour parameters of the visitor or spam bot. These parameters are estimated, and service makes a decision &ndash; to post a message or to define it as spam and reject. On the basis of such checks, the service forms its own list of email addresses used by spam bots. Likewise the registrations of visitors are checked. The service adds not only email addresses to the blacklist, but also IP addresses and domains of the websites that are promoted through spam mailout. All of this happens automatically and requires no action from the administration of the website. At 2.5 million queries the service makes a mistake in 40-45 cases, i.e. CleanTalk detects spam with 99.9982% accuracy. We constantly monitor these errors and make adjustments to our algorithms&rdquo;, so the team is aiming to improve those figures over time.</p>\n<p>All-in-one.&nbsp;CleanTalk immediately protects all forms &ndash; comments, registration, feedback, contacts. No need to install additional plug-ins for each form. You get save resources and increase performance of your website.</p>\n<p>Spam attacks log. Service CleanTalk records all filtered comments, registration and other spam attacks in the &ldquo;Log of spam attacks&rdquo; and stores the data in the log up to 45 days. Using the log, you can ensure reliable protection of your website from spam and no false/positive filtering.</p>\n<p>With the help of anti-spam by CleanTalk you&nbsp;can check existing comments and users, to find and quickly delete spam comments at once.&nbsp;This allows administrators of the Web sites automatically to check and identify spam bots, comments and users, which were not detected by conventional anti-spam tools.&nbsp;Checking existing comments and users is made on the basis of the nearly 2 million of certain spam bots. Detailed statistic allows CleanTalk customers to control the whole process.</p>\n<p>CleanTalk has got an advanced option &ldquo;Spam FireWall&rdquo;, this option allows blocking the most active spam bots before they get access to web site. It prevents loading of pages of the web site by spam bots, so your web server doesn&rsquo;t need perform all scripts on these pages. Also it prevents scanning of pages of the web site spam bots. Therefore Spam FireWall significantly can reduce the load on your web server.&nbsp;Spam FireWall can mitigate HTTP/HTTPS DDoS attacks. When an intruder makes GET requests to attack your website. Spam FireWall blocks all requests from bad IP addresses. Your website gives infringer a special page with description of DDoS rejection instead of the website pages. Therefore Spam FireWall can help to reduce of CPU usage on your server.</p>\n<p>&nbsp;</p>\n<p>CleanTalk dashboard</p>\n<p><img src="../images/presskit/pk3.png" alt="" width="100%" /></p>\n<p>&nbsp;CleanTalk Analitics</p>\n<p><img src="../images/presskit/pk4.png" alt="" width="100%" /></p>\n<p>WordPress CleanTalk plugin settings</p>\n<p><img src="../images/presskit/pk5.png" alt="" width="100%" /></p>\n<p>&nbsp;CleanTalk Logo</p>\n<p><img src="../images/presskit/pk6.png" alt="" width="100%" height="603" /></p>\n<p>CleanTalk icons:</p>\n<p>150x53 px&nbsp;</p>\n<p><img src="../images/icons/150px/Yellow.png" alt="" width="150" height="53" />&nbsp;<img src="../images/icons/150px/Blue.png" alt="" width="150" height="53" />&nbsp;<img src="../images/icons/150px/Green.png" alt="" width="150" height="53" />&nbsp;<img src="../images/icons/150px/Normal.png" alt="" width="150" height="53" />&nbsp;<img src="../images/icons/150px/Purple.png" alt="" width="150" height="53" />&nbsp;<img src="../images/icons/150px/Red.png" alt="" width="150" height="53" />&nbsp;<img src="../images/icons/150px/White.png" alt="" width="150" height="53" /></p>\n<p>&nbsp;</p>\n<p>264х93 px</p>\n<p><img src="../images/icons/264px/Yellow.png" alt="" width="264" height="93" />&nbsp;<img src="../images/icons/264px/Blue.png" alt="" width="264" height="93" />&nbsp;<img src="../images/icons/264px/Green.png" alt="" />&nbsp;<img src="../images/icons/264px/Normal.png" alt="" width="264" height="93" />&nbsp;<img src="../images/icons/264px/Purple.png" alt="" width="264" height="93" />&nbsp;<img src="../images/icons/264px/Red.png" alt="" width="264" height="93" />&nbsp;<img src="../images/icons/264px/White.png" alt="" width="264" height="93" /></p>\n<p>&ldquo;CleanTalk team has been developing a cloud spam protection system for four years and has</p>\n<p>created a truly reliable anti-spam service designed for you to guarantee your safety&rdquo;.</p>\n<p><strong>Contacts</strong>:</p>\n<p>CleanTalk Inc</p>\n<p><a href="https://twitter.com/cleantalk_en">Twitter</a></p>\n<p><a href="https://www.facebook.com/CleanTalk-1376135232685216/?ref=hl">Facebook</a></p>\n<p>Email <a href="mailto:welcome@cleantalk.org">welcome@cleantalk.org</a></p>', 'Presskit', 'Presskit', '2017-10-25 11:42:21', NULL, NULL),
(14, 0, 'docs', 'Добавление нового антиспам приложения', '<h1>Добавление нового антиспам приложения</h1>\r\n<p>&nbsp;</p>\r\n<p>1. Откывать форму добавления приложения,</p>\r\n<p><a href="antispam-apps?action=new">https://cleantalk.org/noc/antispam-apps?action=new</a></p>\r\n<p>2. Указать параметры нового прилжоения,</p>\r\n<ul>\r\n<li>Дата выпуска - дата релиза приложения в формате.</li>\r\n<li>Версия релиза - версия приложения для отображения в Панели управления, рассылках. К примеру: "5.32".</li>\r\n<li>CMS - к которой привязать приложение.</li>\r\n<li>Агент - строка agent из кода приложения. По этой строке происходит привязка сайта к тому или иному приложению.</li>\r\n<li>Продуктивная версия - если указано "Да", то интерфейс сбросит установит новому приложению статус продуктивного, что автоматически создат оповещения в рассылках и ПУ, о необходимости обновиться пользователям с более старыми приложениями для этой же CMS.</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p>3. Нажать кнопку "Сохранить".</p>\r\n<p>4. Проверить в Панели управления, что приложение привязалось к тестовому сайту с тем же agent.</p>', '', '', '2017-10-25 11:42:21', NULL, NULL),
(15, 119, 'help', 'API Method spam_check', '<h1>API Method&nbsp;spam_check</h1>\n<p>This method should be used only for mass check IPs, emails for spam activity. For other purposes you could use other methods:</p>\n<ul>\n<li><a href="/help/api-check-message">check_message</a>&nbsp;- API spam filter to check Posts/comments.</li>\n<li><a href="/help/api-check-newuser">check_newuser</a>&nbsp;- Registrations check.</li>\n<li><a href="/api-send-feedback">send_feedback</a> - Send feedback to CleanTalk.</li>\n<li><a href="/help/api-backlinks-check">backlinks_check</a> - mass check for backlinks in spam comments.</li>\n</ul>\n<p><strong>Method result is an information about existing of IP/email records in our database for all time</strong>. It may differ from results of blacklist checking on our site -&nbsp;<a href="/blacklists">https://cleantalk.org/blacklists </a>- which shows current spam status only.</p>\n<h2>Call required GET params:</h2>\n<ul>\n<li>method_name - should be ''spam_check'',</li>\n<li>auth_key - <abbr title="Application Programming Interface">API</abbr> key. To obtain a key please get an account here <a class="urlextern" title="https://cleantalk.org/register?platform=api" href="/register?platform=api" rel="nofollow">https://cleantalk.org/register?platform=api</a>.</li>\n</ul>\n<h2>Optional GET params:</h2>\n<ul>\n<li>ip - ip address to check</li>\n<li>email - email address to check (The result is given for the last 6 months)</li>\n<li>date - date to check in YYYY-MM-DD format (It can be applied only to IP addresses)</li>\n</ul>\n<p>Example request with IP:</p>\n<p>beginplain</p>\n<p>https://api.cleantalk.org/?method_name=spam_check&amp;auth_key=123456&amp;email=stop_email@example.com&amp;ip=127.0.0.1</p>\n<p>endplain</p>\n<p>Example request with date:</p>\n<p>beginplain</p>\n<p>https://api.cleantalk.org/?method_name=spam_check&amp;auth_key=123456&amp;email=stop_email@example.com&amp;ip=127.0.0.1&amp;date=2017-01-31</p>\n<p>endplain</p>\n<p>API returns JSON string, for example:</p>\n<p>beginjs</p>\n<p>{"data":{"127.0.0.1":{"appears":0},"stop_email@example.com":{"appears":1,"frequency":"999","spam_rate":"100","updated":"2019-04-24 23:33:00","frequency_time_10m":"1","frequency_time_1h":"10","frequency_time_24h":"1000"}}}</p>\n<p>endjs</p>\n<p>&nbsp;</p>\n<h2>Response explanation:</h2>\n<ul>\n<li>data - array with checked records,</li>\n<li>appears - marker witch define record status in the blacklists 0|1,</li>\n<li>spam_rate - a rating of spam activity from 0 to 100%. 100 means certain spam,</li>\n<li>frequency - is a number of web-sites that reported about spam activity of the record. It can be from 0 up to 9999,</li>\n<li>frequency_time_10m - 10 minutes activity,</li>\n<li>frequency_time_1h - 1 hour activity,</li>\n<li>frequency_time_24h - 24 hours activity.</li>\n</ul>\n<p>In case of ''date'' parameter response contains results on given date only.</p>\n<h2 id="multiple_records_check" class="sectionedit2">Multiple records check</h2>\n<p>You can submit multiple records to test per 1 call, to do that use POST options:</p>\n<ul>\n<li>data - string with records to check separated by '',''.</li>\n</ul>\n<p>Example,</p>\n<p>beginjs</p>\n<p>wget -O- --post-data=''data=stop_email@example.com,10.0.0.1,10.0.0.2'' https://api.cleantalk.org/?method_name=spam_check\\&amp;auth_key=123456</p>\n<p>endjs</p>\n<p>&nbsp;</p>\n<p>Response:</p>\n<p>beginplain</p>\n<p>{"data":{"stop_email@example.com":{"appears":1,"frequency":"999","updated":"2019-04-24 23:33:00"},"10.0.0.1":{"appears":0},"10.0.0.2":{"appears":0}}}</p>\n<p>endplain</p>\n<p>&nbsp;</p>\n<h2 id="restrictions" class="sectionedit3">Restrictions</h2>\n<p>If you get calls limit, <abbr title="Application Programming Interface">API</abbr> returns error notice. Example:</p>\n<p>beginplain</p>\n<p>{"error_message":"Calls limit exceeded.","error_no":10}</p>\n<p>endplain</p>\n<p>Current calls limit is 100 per 60 seconds.</p>\n<p>&nbsp;</p>\n<p>If you get data elements limit in <strong>spam_check</strong> method, <abbr title="Application Programming Interface">API</abbr> returns error notice. Example:</p>\n<p>beginplain</p>\n<p>{"error_message":"Recevied 1001 records to check, maximum 1000 records check perl call.","error_no":8}</p>\n<p>endplain</p>\n<p>Current data elements limit is 1000.</p>\n<p>&nbsp;</p>\n<p>Recommended timeout is no more than 180 seconds.</p>\n<p><strong>Notice:</strong>&nbsp;if the date is sent in the request, then the request may be implemented a little longer.</p>\n<p>&nbsp;</p>\n<h2>Description of several examples of API responses</h2>\n<p><br />Below you will find several examples and description of the parameters for possible server response options, such as:</p>\n<ul>\n<li>"appears" - marker wich define record status in the blacklists 0|1;</li>\n<li>"spam_rate" - a rating of spam activity from 0 to 100%. 100 means certain spam;</li>\n<li>"frequency" - is a number of web-sites that reported about spam activity of the record. It can be from 0 up to 9999;</li>\n<li>"frequency_time_10m" - 10 minutes activity;</li>\n<li>"frequency_time_1h" - 1 hour activity;</li>\n<li>"frequency_time_24h" - 24 hours activity.</li>\n</ul>\n<p>&nbsp;</p>\n<p>Example:</p>\n<p>beginplain</p>\n<p>"ip":{"appears":1}</p>\n<p>endplain</p>\n<p>Exlanation:</p>\n<ul>\n<li>"appears":1 - IP is in blacklist;</li>\n</ul>\n<p>&nbsp;</p>\n<p>Example:</p>\n<p>beginplain</p>\n<p>"ip":{"appears":0}</p>\n<p>endplain</p>\n<p>Exlanation:</p>\n<ul>\n<li>"appears":0 - IP is not in blacklist;</li>\n</ul>\n<p>&nbsp;</p>\n<p>Example:</p>\n<p>beginplain</p>\n<p>"ip":{"appears":0, "frequency":"15"}</p>\n<p>endplain</p>\n<p>Exlanation:</p>\n<ul>\n<li>"appears":"0" - IP is not in blacklist;</li>\n<li>"frequency":"15" - 15 web-sites reported about spam activity of this IP.</li>\n</ul>\n<p>&nbsp;</p>\n<p>Example:</p>\n<p>beginplain</p>\n<p>"ip":{ "appears":"0","spam_rate":"1","frequency":"1","frequency_time_10m":"1","frequency_time_1h":"1","frequency_time_24h":"1"}</p>\n<p>endplain</p>\n<p>Explanation:</p>\n<ul>\n<li>"appears":0 - IP is not in blacklist;</li>\n<li>"spam_rate":"1" - there was 1 request and it was detected as spam;</li>\n<li>"frequency":"1" - 1 web-site reported about spam activity of this IP;</li>\n<li>"frequency_time_10m":"1" - there was 1 spam request time in 10 minutes;</li>\n<li>"frequency_time_1h":"1" - there was 1 spam request in 1 hour;</li>\n<li>"frequency_time_24h":"1" - there was 1 spam request in 24 hours.</li>\n</ul>\n<p>Example:&nbsp;</p>\n<p>beginplain</p>\n<p>"email":{ "appears":0,"spam_rate":"1","frequency":"1","frequency_time_10m":"1","frequency_time_1h":"1","frequency_time_24h":"1","exists":"1"}</p>\n<p>endplain</p>\n<p>Explanation:</p>\n<ul>\n<li>"appears":"0" - email is not in blacklist;</li>\n<li>"spam_rate":"1" - there was 1 request and it was detected as spam;</li>\n<li>"frequency":"1" - 1 web-site reported about spam activity of this email;</li>\n<li>"frequency_time_10m":"1" - there was 1 spam request time in 10 minutes;</li>\n<li>"frequency_time_1h":"1" - there was 1 spam request in 1 hour;</li>\n<li>"frequency_time_24h":"1" - there was 1 spam request in 24 hours.</li>\n<li>"exists":"1" - the real email that exists on a mail server.</li>\n</ul>\n<p>&nbsp;</p>', 'api, spam_check, spam, check, mass', 'API Method spam_check', '2018-03-19 12:20:20', NULL, NULL),
(17, 0, 'docs', 'Чеклист запуска новой вебноды (WEBNODE_X)', '<h1><strong>Чеклист запуска новой вебноды (WEBNODE_X)</strong></h1>\r\n<p>&nbsp;</p>\r\n<p>- Сделать локальную копию транка,</p>\r\n<p>beginplain</p>\r\n<p>svn co svn://svn.cleantalk.org/cleantalk.ru/trunk/htdocs</p>\r\n<p>endplain</p>\r\n<p>&nbsp;</p>\r\n<p>- Установить настроить php-fpm + nginx. Настройки php-fpm, nginx смотри на webnode1.</p>\r\n<p>- Проверить отправку почты через вебсайт. Почта должна приходить&nbsp;на пубулиные хостинги (gmail.com) без каких-либо замечаний в заголовках.</p>\r\n<p>- Проверить работу GeoIP, GeoIPOrg в черных списках. GeoIPOrg должен отратабывать иначе не будет работать исключение по неограничению поисковиков.</p>', '', '', '2017-10-25 11:42:21', NULL, NULL),
(23, 0, 'docs', 'SpamFireWall', '<h1>Результат выдачи для отключенных акаунтов</h1>\r\n<p>&nbsp;</p>\r\n<p>Отключенным аккаунтам метод 2s_blacklists_db() выдает список из 10-20 адресов, которые нигде не используются в Сети. Таким образом обнуляется локальная база SFW при отключении сервиса. Пример,</p>\r\n<p>&nbsp;</p>\r\n<p>beginplain</p>\r\n<p>https://api.cleantalk.org/?method_name=2s_blacklists_db&amp;auth_key=va4aqusujeby <br /><br /> {"data":[[184483329,4294967295],[184483330,4294967295],[184483331,4294967295],[184483332,4294967295],[184483333,4294967295],[184483334,4294967295],[184483335,4294967295],[184483336,4294967295],[184483337,4294967295],[184483338,4294967295],[184483339,4294967295],[184483340,4294967295]]}</p>\r\n<p>endplain</p>', '', '', '2017-10-25 11:42:21', NULL, NULL),
(27, 129, 'help', 'Для хостинг провайдеров', '<h1>Для партнеров&nbsp;</h1>\n<p>CleanTalk занимается защитой от спама более 5 лет. &nbsp;CleanTalk - это облачный сервис защиты от спам ботов для веб сайтов.&nbsp;CleanTalk использует методы защиты, которые незаметны для посетителей веб сайта. Защита от спам ботов востребована и необходима для большинства веб сайтов.</p>\n<h2>Мы предлагаем вам снизить расходы ресурсов на хостинг за счет использования решений предлагаемых компанией CleanTalk</h2>\n<p>CleanTalk предлагает вам предоставить для ваших клиентов одну из самых надежных систем защиты от спам ботов. Это позволит вам расширить ваши тарифы дополнительным бесплатным сервисом и сделать их более привлекательными. Предложить своим клиентам больше, чем ваши конкуренты (все в одном, бесплатно для клиентов).</p>\n<p>В наших последних версиях плагинов для CMS мы добавили новую опцию Spam FireWall, которая позволяет блокировать не только POST запросы, но и GET запросы на веб сайт от спам ботов. Эта функция, кроме непосредственной фильтрации спама, снижает нагрузку на CPU хостинг-сервера и на сеть передачи данных в пределах 7-30% (в зависимости от уровня спам трафика на том или ином вебсайте).&nbsp;CleanTalk анти спам фильтрует спам ботов в тот момент когда они пытаются разместить на веб сайте комментарий, зарегистрироваться, т.е. делают POST запрос. В некоторых случаях спам боты отправляют не только POST запросы, но и GET, т.е. загружают страницы веб сайта и тем самым нагружают сайт запросами.</p>\n<p>Эффект снижения возникает в результате фильтрации спам трафика до загрузки основого кода сайта, что приводит к снижению нагрузки на CPU сервера и на отдаваемый трафик в сеть. Кроме спам ботов, так же будут блокироваться все HTTP/HTTPS запросы со спам активных IP адресов. Это помогает блокировать HTTP DDoS атаки, SQL атаки, сканирование сайта и т.д.&nbsp;&nbsp;</p>\n<div>Сейчас в базе более 20 млн. спам активных IP адресов, 203 тыс. сетей, 30 тыс. автономных систем, 10 млн. email адресов, почти 6 млн. почтовых доменов.&nbsp;В сутки блокируем более 2млн. спам атак, точность фильтрации выше 99.998%.</div>\n<h2>Идея в том, чтобы бесплатно добавить антиспам CleanTalk к тарифам на хостинг. С целью,</h2>\n<ul>\n<li>повысить ценность хостинга для вашего клиента, т.к. он поднимет ценность хостинга для ваших клиентов, т.к. за те же условные 250 рублей клиент получает больший набор услуг и получает бесплатно антиспам, который для всех остальных стоит 550 рублей в год.</li>\n<li>повысить лояльность к хостингу (т.к. переход на другой хостинг вынудит либо отказаться от проверенной антиспам услуги, либо оплатить ее полную стоимость на нашем сайте).</li>\n<li>повысить количество клиентов на еденицу оборудования/полосы за счет применения технологии Spam FireWall.</li>\n</ul>\n<div>\n<div>CleanTalk фильтрует спам без использования каких-либо видимых средств защиты, т.е. CleanTalk не перекладывает проблему защиты от спама на плечи посетителя веб сайта (как это делают CAPTCHA решения).</div>\n<div>&nbsp;</div>\n<div>Со своей стороны мы готовы уведомить наших клиентов, о возможности получить бесплатный CleanTalk на вашем хостинге и о вашем специальном предложении, при переносе сайтов на ваш хостинг. Мы проведем емайл рассылку по всем нашим клиентам(конверсия в открытия 22-24%) и разместим ваш баннер на этой странице.</div>\n<div>&nbsp;</div>\n<h2>Протестировать можно так:</h2>\n<div>1. Зарегистрируйте акаунт,<br /><br /><a href="http://cleantalk.org/register">http://cleantalk.org/register</a></div>\n<div>&nbsp;</div>\n<div>2. Добавьте IP адрес в Панели управления,<br /><br /><a href="/my/?cp_mode=hosting-antispam">https://cleantalk.org/my/?cp_mode=hosting-antispam</a></div>\n<div>&nbsp;</div>\n<div>3. Установите на сайте один из готовых модулей,<br /><br /><a href="/install">https://cleantalk.org/install</a>\n<div>&nbsp;&nbsp;</div>\n</div>\n<div>Клиентам остается только установить плагин CleanTalk на сайт, и он сразу будет работать. CleanTalk обеспечивает тех поддержку и обслуживание клиентов самостоятельно.</div>\n<div>&nbsp;</div>\n<h2>Наши партнеры предоставляющие CleanTalk бесплатно:</h2>\n<div>&nbsp;</div>\n<div><a href="http://hosterbox.ru/promo/cleantalk2016/"><img src="http://cleantalk.org/images/hosterbox.png" alt="Hosterbox.ru" width="212" height="26" /></a>&nbsp;</div>\n<div>&nbsp;</div>\n<div>&nbsp;</div>\n<div><strong>Если у вас остались вопросы вы всегда можете с нами&nbsp;связаться написав на почту <a href="mailto:welcome@cleantalk.org">welcome@cleantalk.org</a>.</strong></div>\n<div>&nbsp;</div>\n<div>&nbsp;</div>\n<div>&nbsp;</div>\n<h3>Партнерский модуль совместных покупок для форумов phpbb 3.0</h3>\n<p><a href="/help/partner-script"><img src="http://sp.dim-studio.ru/images/VPROsmall.gif" alt="" width="196" height="46" /></a></p>\n<div>&nbsp;</div>\n</div>', 'cleantalk, партнер, партнерство, hosterbox', 'Для партнеров', '2017-10-25 11:42:21', NULL, NULL),
(29, 0, 'docs', 'Инструкция по конкатенации и минификации CSS файлов Gulp', '<p>1. Устанавливаем Node JS <a href="https://nodejs.org/en/download/" target="_blank">https://nodejs.org/en/download/</a></p>\r\n<p>2. С помощью пакетного менеджера npm устанавливаем Gulp глобально</p>\r\n<p><strong> npm install --global gulp-cli</strong></p>\r\n<p>3. Переходим в папку проекта и создаем файл package.json следующего содержания</p>\r\n<p>beginjs</p>\r\n<p>{<br />&nbsp; "name": "gulp-project",<br />&nbsp; "version": "0.1.0",<br />&nbsp; "devDependencies": {<br />&nbsp;&nbsp;&nbsp; <br />&nbsp; }<br />}</p>\r\n<p>endjs</p>\r\n<p>4. В той же самой папке проекта устанавливаем Gulp локально</p>\r\n<p><strong>npm install --save-dev gulp</strong></p>\r\n<p>Ключ --save-dev значит что информация о пакете будет добавлена в раздел devDependencies файла package.json</p>\r\n<p>5. Устанавливаем плагин gulp-concat-css</p>\r\n<p><strong>npm install --save-dev gulp-concat-css</strong></p>\r\n<p>6. Устанавливаем плагин gulp-minify-css</p>\r\n<p>&nbsp;<strong>npm install --save-dev gulp-minify-css</strong></p>\r\n<p>7. Создаем файл gulpfile.js (лежит в репозитории в корне) следующего содержания</p>\r\n<p>beginjs</p>\r\n<p>var gulp = require(''gulp'');<br />var&nbsp;&nbsp; &nbsp;concatCss = require(''gulp-concat-css'');<br />var&nbsp;&nbsp; &nbsp;minifyCss = require(''gulp-minify-css'');<br /><br />gulp.task(''default'', function() {<br />&nbsp;&nbsp;&nbsp; &nbsp;return gulp.src([''assetsmobile/css/main.css'',''assetsmobile/css/modal.css'',''assetsmobile/css/cleantalk.css''])<br />&nbsp;&nbsp;&nbsp; .pipe(concatCss(''all.css''))<br />&nbsp;&nbsp;&nbsp; .pipe(minifyCss())<br />&nbsp;&nbsp;&nbsp; .pipe(gulp.dest(''assetsmobile/css/''));<br />});</p>\r\n<p>endjs</p>\r\n<p>8. Даём в консоли команду gulp и на выходе получаем обьединённый и минифицированный css файл</p>\r\n<p>ВАЖНЫЙ МОМЕНТ!!!</p>\r\n<p>Gulp выдаёт ошибку если нет файла normalize.css в папке assetsmobile/vendor/normalize.css - те нужно создать папки vendor/normalize.css в папке assetsmobile и кинуть туда файл normalize.css отсюда <a href="https://necolas.github.io/normalize.css/" target="_blank">https://necolas.github.io/normalize.css/</a></p>', '', '', '2017-10-25 11:42:21', NULL, NULL),
(31, 131, '0', 'How to move service to another webiste?', '<p>To move&nbsp;service to another website you must do following things:</p>\r\n<p>1) Go to service settings.</p>\r\n<p>2) Change URL of the website.</p>\r\n<p>3) Change CMS of the website</p>\r\n<p>4) Apply changes.</p>', '', '', '2017-10-25 11:42:21', NULL, NULL),
(33, 133, 'help', 'Раздел помощи CleanTalk. Вопросы, ответы, инструкции.', '<h1 class="help-content__title --1">Раздел помощи CleanTalk. Вопросы, ответы, инструкции.</h1>\n<p>&nbsp;</p>\n<h4>АнтиСпам сервис</h4>\n<div class="help-content__row">\n<div class="help-content__right">\n<ul class="help-content__list">\n<li><a href="/premium-notice">Премиум уведомление осталось в WordPress после оплаты</a></li>\n<li><a href="/faq#test">Как можно протестировать антиспам на сайте?</a></li>\n<li><a href="/faq#more_spam">После установки я заметил, что количество спам-атак увеличилось</a></li>\n<li><a href="/help/forbidden-please-enable-javascript">Вижу в логах массу сообщений следующего типа "Forbidden. Please enable JavaScript. Spam sender name."</a></li>\n<li><a href="/help/jcomments-spam">Почему CleanTalk пропускает спам в JComments?</a></li>\n<li><a href="/help/GreyList">Что такое Grey List?</a></li>\n<li><a href="/help/WordPress-plugin-settings">WordPress: Настройки антиспам плагина от CleanTalk</a></li>\n<li><a href="/help/deletion-from-public-page">Как удалить свой IP или почтовый адрес с публичной страницы чёрных списков CleanTalk?</a></li>\n</ul>\n<p>&nbsp;</p>\n<h4>АнтиСпам пакеты и платежная информация</h4>\n<ul class="help-content__list">\n<li><a href="/faq#need_more">Могу ли я заказать специальные варианты защиты от спама?</a></li>\n<li><a href="/faq#double_connect">Я хотел бы подключить два сайта к сервису. Надо ли регистрировать два аккаунта или можно использовать один и тот же ключ?</a></li>\n<li><a href="/help/webmoney-payment">Как оплатить сервис CleanTalk с помощью WebMoney?</a></li>\n<li><a href="/help/extra-package">Расширенный пакет опций CleanTalk</a></li>\n<li><a href="/faq#not_connected">Статус моего сайта "Not connected"!</a></li>\n<li><a href="/licence-for-multisite-environment-wpmu">Какой пакет выбрать для WPMU</a></li>\n<li><a title="Партнёрская Программа от CleanTalk" href="/affiliate-program">Есть ли Партнёрская Программа у CleanTalk?</a></li>\n<li><a title="Как оплатить сервис безналично (по счету)?" href="https://cleantalk.org/help/how-to-pay-by-invoice">Как оплатить сервис безналично (по счету)?</a></li>\n<li><a href="/usage/hosting-antispam">Тариф Хостинг Антиспам</a></li>\n</ul>\n<p>&nbsp;</p>\n<h4>Управление аккаунтом</h4>\n<ul class="help-content__list">\n<li><a href="/help/access-to-dashboard">Как восстановить доступ к антиспам консоли?</a></li>\n<li><a href="/faq#feedback_spam">Как мне сообщить о пропущенном спаме или неправильном фильтре?</a></li>\n<li><a href="/help/I-cant-find-message">Не могу найти сообщение в панели управления сайта</a></li>\n<li><a href="/help/my-ip-blacklisted">Мой IP попал в чёрный список</a></li>\n<li><a href="/help/I-want-to-block-ip">Хочу заблокировать IP/email/IP сеть</a></li>\n<li><a href="/help/store-the-content-of-messages">Я не хочу хранить содержание сообщений в логах</a></li>\n<li><a href="/help/change-service-url">Как изменить сайт к которому подключен сервис?</a></li>\n<li><a href="/help/blacklist-usage">Персональные черные списки</a></li>\n<li><a href="/stop-word">Опция "Стоп-Слова"</a></li>\n<li><a href="/sfw-blocks-networks">SpamFireWall - Блокировка подсетей</a></li>\n<li><a href="/delegation">Делегирование сайтов другим пользователям</a></li>\n<li><a href="/Anti-Spam-Log">Как пользоваться Антиспам Журналом</a></li>\n<li><a href="/spam-comment-management">Управление спам-комментариями</a></li>\n<li><a href="/help/overwrite-message-for-blocked-visitors">Как изменить сообщение для заблокированных посетителей</a></li>\n</ul>\n<p>&nbsp;</p>\n<h4>Другие вопросы</h4>\n<ul class="help-content__list">\n<li><a href="/help/review-help-main">Как написать отзыв о CleanTalk и получить бонусные месяцы?</a></li>\n<li><a title="CleanTalk и CloudFlare совместимы?" href="/help/cleantalk-cloudflare">CloudFlare и&nbsp;CleanTalk&nbsp;совместимы?</a></li>\n<li><a href="/help/cleantalk-vs-akismet">Сравнение CleanTalk и Akismet</a></li>\n</ul>\n<p>&nbsp;</p>\n<h4>Сервис&nbsp;безопасности</h4>\n<ul class="help-content__list">\n<li><a href="/Security-Log">Как пользоваться Журналом Безопасности</a></li>\n<li><a href="/help/Security-Firewall">Опция "Security Firewall"</a></li>\n<li><a href="/help/security-malware-scanner">Сканер вредоносного кода</a></li>\n</ul>\n<p>&nbsp;</p>\n<div class="help-ways">\n<h3 class="help-ways__title">Куда обратиться за помощью?</h3>\n<ul class="help-ways__list">\n<li class="help-ways__item --1">\n<div class="help-ways__item__title"><a href="/my/support">Cлужба технической поддержки</a></div>\n<div class="help-ways__item__content">\n<div class="help-ways__item__title">Создайте обращение</div>\n</div>\n</li>\n<li class="help-ways__item --1">\n<div class="help-ways__item__title"><a class="help-ways__item__link" href="/forum/">Форум</a></div>\n<div class="help-ways__item__content">Задайте вопрос на форуме</div>\n</li>\n<li class="help-ways__item --2">\n<div class="help-ways__item__title"><a class="help-ways__item__link" href="/blog">Блог</a></div>\n<div class="help-ways__item__content">Последние новости в нашем блоге</div>\n</li>\n</ul>\n</div>\n</div>\n</div>', 'cleantalk, антиспам, установка, инструкция, помощь, faq, чаво, help', 'Раздел помощи CleanTalk. Вопросы, ответы, инструкции.', '2018-04-06 18:40:58', NULL, NULL),
(373, 469, 'help', 'Does CleanTalk have an Affiliate Program?', '<h1>Does CleanTalk have an Affiliate Program?</h1>\n<p>&nbsp;</p>\n<p>Yes, we do.</p>\n<p>&nbsp;</p>\n<p>Our conditions:</p>\n<ol>\n<li>For the first 10 referrals you earn&nbsp;30% of each referral payment to your balance.</li>\n<li>Starting with 11th referral you earn 70% of each referral payment to your balance.</li>\n<li>When you get 50 dollars on your balance, you can transfer your money to your PayPal account.</li>\n</ol>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<h3>How to use the CleanTalk&nbsp;Affiliate Program?</h3>\n<p>&nbsp;</p>\n<p>It''s very simple. Enter to your&nbsp;<a title="CleanTalk Control Panel" href="/my/" target="_blank">CleanTalk Control Panel</a>, press the string of your e-mail on the upper right corner to&nbsp;unroll the menu and choose&nbsp;"Affiliate program".</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/affiliate+program/8mvJCbu.png" alt="" width="900" height="352" /></p>\n<p>&nbsp;</p>\n<p>You will see the page with your Affiliate Link, statistics panel, list of your referrals, their payments, list of buttons&nbsp;and your balance.</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/affiliate+program/oDWmygq.png" alt="" width="900" height="715" /></p>\n<p>&nbsp;</p>\n<p>Use the received Affiliate Link to create new referrals.</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<div>You can ask us any question via:</div>\n<div>&nbsp;&mdash; E-mail [ support@cleantalk.org ].</div>\n<div>&nbsp;&mdash; Request [ <a href="/my/support" target="_blank">https://cleantalk.org/my/support</a> ].</div>\n<div>&nbsp;&mdash; Forum [ <a href="https://community.cleantalk.org/" target="_blank">https://community.cleantalk.org/</a> ].</div>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<div class="button_go"><a class="button --blue" href="/my">Go to Dashboard</a></div>\n<p>&nbsp;</p>', 'affiliate, affiliate program, affiliate link, referrals, referral links, PayPal', 'Does CleanTalk have an Affiliate Program?', '2018-03-26 12:36:45', NULL, NULL);
INSERT INTO `articles` (`article_id`, `article_linkid`, `article_where`, `article_title`, `article_content`, `keywords`, `description`, `updated`, `widget`, `widget_url`) VALUES
(375, 471, 'help', 'Есть ли Партнёрская Программа у CleanTalk?', '<h1>Есть ли Партнёрская Программа у CleanTalk?</h1>\n<p>&nbsp;</p>\n<p>Да, есть.</p>\n<p>&nbsp;</p>\n<p>Наши условия следующие:</p>\n<ol>\n<li>За первые 10 рефералов Вы получаете 30% от каждого платежа реферала на свой баланс.</li>\n<li>С 11-го реферала и далее Вы получаете 70% от каждого платежа&nbsp;реферала на свой баланс.</li>\n<li>При накоплении&nbsp;1000 рублей на своём балансе, Вы можете вывести их безналичным платежом на PayPal, Яндекс.Деньги, WebMoney.</li>\n</ol>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<h3>Как воспользоваться Партнёрской Программой?</h3>\n<p>&nbsp;</p>\n<p>Очень просто. Зайдите в&nbsp;Вашу <a title="Панель Управления CleanTalk" href="/my/" target="_blank">панель управления CleanTalk</a>, нажмите на&nbsp;строку со своей почтой в правом верхнем углу, чтобы&nbsp;развернуть меню, и выберите пункт "Партнёрская программа".</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/affiliate+program/TD7TqCt.png" alt="" width="900" height="313" /></p>\n<p>&nbsp;</p>\n<p>Откроется страница с Вашей партнёрской ссылкой, панелью статистики, списком рефералов, их платежами, тематическими ссылками-кнопками и Вашим балансом.</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/affiliate+program/02BtK7k.png" alt="" width="900" height="715" /></p>\n<p>&nbsp;</p>\n<p>Используйте полученную партнёрскую ссылку для создания рефералов.</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<div>Если возникнут какие-либо вопросы, то Вы можете задать их через:</div>\n<div>&nbsp;&mdash; Почту [ support@cleantalk.org ].</div>\n<div>&nbsp;&mdash; Обращение [ <a href="/my/support" target="_blank">https://cleantalk.org/my/support</a> ].</div>\n<div>&nbsp;&mdash; Форум [ <a href="https://community.cleantalk.org/" target="_blank">https://community.cleantalk.org/</a> ].</div>\n<p>&nbsp;</p>\n<div class="button_go"><a class="button --blue" href="/my">Перейти в панель управления</a></div>\n<p>&nbsp;</p>', 'партнёры, партнёрская программа, партнёрская ссылка, рефералы, реферальные ссылки, PayPal', 'Есть ли Партнёрская Программа у CleanTalk?', '2018-03-26 12:37:19', NULL, NULL),
(35, 0, 'docs', 'Генерация рассылок', '<p style="text-align: center;"><strong>Генерация рассылок</strong></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-weight: 400;">1. Добавить шаблоны в таблицу massmail_data. В шаблонах используются %s для мест вставки строк данных. В шаблоне типа body_template нужно код трекера добавлять в самый конец, перед &lt;/body&gt;, чтоб его %s был последний в шаблоне.</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-weight: 400;">2. Добавить данные в эту же таблицу в виде JSON-закодированных массивов. Для данных типа body_values массив должен содержать на 1 меньше значений, т.к. последним будет добавлено значение трекера.</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-weight: 400;">3. Создать рассылку в таблице massmails, заполнив нужные id значениями id из таблицы massmail_data. ВНИМАНИЕ - поле state пока не учитывается.</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-weight: 400;">4. Скриптом bin/massmail-userlist-generate.pl сделать список клиентов для рассылки. Править sql-запрос для достижения нужного эффекта. Вывод перенаправить в файл. Пример файла - files/massmail-userlist-example.txt. Поля файла - email, user_id и уникальный код трекера, который будет добавлен в массив данных типа body_values и подставлен в body_template.</span></p>\r\n<p><strong>./massmail-userlist-generate.pl &gt; massmail-userlist.txt</strong></p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-weight: 400;">5. Скриптом bin/massmail-messages-generate.pl сделать список готовых к отправке почтовых сообщений в таблице massmail_messages. Параметр скрипта -m &lt;massmail_id&gt; идентификатор созданной рассылки из таблицы massmails. Список клиентов подаётся на stdin.&nbsp;</span></p>\r\n<p><strong>cat massmail-userlist.txt | ./massmail-messages-generate.pl -m 999</strong></p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-weight: 400;">Сразу после этого в течение 5 минут новые сообщения будут приняты к отправке почтовым сервером!</span></p>\r\n<p><br /><br /></p>', '', '', '2017-10-25 11:42:21', NULL, NULL),
(39, 137, 'help', 'How to Move Cleantalk Service to Another Webiste?', '<h1>How to Move Cleantalk Service to Another Website?</h1>\n<h2>You should do the following things for that:</h2>\n<p>1. Enter to your <a href="/my">Cleantalk Dashboard</a>.</p>\n<p>2. Go to&nbsp;the settings of service you want to&nbsp;move to another website:</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/dashboard_en/dashboard_settings.png" alt="Enter settings" width="800" height="313" /></p>\n<p>3.1. Fill URL field with new URL address of your website.</p>\n<p>3.2.&nbsp;(Optional) Add&nbsp;desription to the service.</p>\n<p>3.3.&nbsp;Choose the CMS platform.</p>\n<p>3.4. Set response language. By default is English.</p>\n<p>3.5.&nbsp;(Optional) Set "Allow HTTP links in comments".</p>\n<p>3.6. (Optional) Set "Enable comments test via stop list".</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/dashboard_en/service+settings/service_instructionChangeSite.png" alt="Service settings" width="800" height="399" /></p>\n<p>3.7.&nbsp;Click "Save" button.</p>\n<p>4.&nbsp;Enter "Access key" in administration panel on your website.</p>\n<p>&nbsp;</p>\n<p><strong>If you still have questions you can always ask our Tech support by&nbsp;creating a ticket&nbsp;<a href="/my/support">https://cleantalk.org/my/support</a>.</strong></p>', 'service, cleantalk, move, another, site, website, help', 'Move Cleantalk Service to Another Website', '2017-10-25 11:42:21', NULL, NULL),
(41, 139, 'help', 'Как изменить сайт к которому подключен сервис Cleantalk?', '<h2>Что бы изменить адрес работы сервиса Cleantalk нужно:</h2>\r\n<p>1. Войти в <a href="/my">панель управления CleanTalk</a>.</p>\r\n<p>2. Открыть настройки нужного вам сервиса (сайта к которому он подключен на данный момент).</p>\r\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/dashboard_ru/dashboard_settings.png" alt="Панель управления - настройки сервиса" width="1000" height="402" /></p>\r\n<p>3.1. Изменить текущий&nbsp;адрес сайта на искомый.</p>\r\n<p>3.2. (Не обязательно) Укажите описание для вашего сайта.</p>\r\n<p>3.3. Выбрать CMS (Joomla, Drupal, Wordpress и др.) которая используется на новом сайте.</p>\r\n<p>3.4. Установите язык ответа пользователю в случае блокирования. (По умолчанию используется английский)</p>\r\n<p>3.5. (Не обязательно) Разрешить HTTP ссылки в сообщениях.</p>\r\n<p>3.6. (Не обязательно) Включить проверку сообщений на стоп-слова.&nbsp;</p>\r\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/dashboard_ru/settings/settings_instruction.png" alt="Настройки сервиса" width="1000" height="510" /></p>\r\n<p>4. Нажмите кнопку "Сохранить".</p>\r\n<p>5. Не забудьте указать ключ доступа в&nbsp;панели администратора на вашем сайте (Нажмите&nbsp;<strong>Кнопку 8</strong>, что бы показать ключ)</p>\r\n<p>&nbsp;</p>\r\n<p>Так же рекомендуем изучить&nbsp;основные настройки личного кабинета и настроить сервис под себя (логирование, отправка отчетов и&nbsp;прочее).</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Если у вас остались вопросы вы всегда можете связаться со службой технической поддержки создав обращение <a href="/my/support">https://cleantalk.org/my/support</a>.</strong></p>', 'cleantalk, изменить, адрес, сайт, сервис', 'Что бы изменить адрес работы сервиса Cleantalk нужно:', '2017-10-25 11:42:21', NULL, NULL),
(43, 141, 'help', 'For partners', '<h1>For Partners</h1>\r\n<p>CleanTalk engaged in spam protection for more than 5 years. CleanTalk is a cloud service protection from spam bots for websites. CleanTalk uses protection methods that are invisible to the visitors of the website. Protection from spam bots popular and essential for most websites.&nbsp;</p>\r\n<h2>We offer you to reduce the costs of hosting resources through the use of solutions offered by CleanTalk.</h2>\r\n<p>CleanTalk offers you to provide for your customers one of the most reliable systems of protection from spam bots. This will allow you to expand your rates additional free service and make them more attractive. To offer its customers more than your competitors (all in one, free of charge to customers).</p>\r\n<p>In our latest versions of plugins for a CMS we have added a new option Spam FireWall, which allows you to block not only POST requests, but GET requests for a website from spam bots. This function, in addition to spam filtering, reduces the load on the CPU of the hosting server and the data network within 7-30% (depending on the level of spam traffic to a particular website). CleanTalk anti spam filters the spam bots at the moment when they are trying to post a comment on a web site, sign up, i.e. doing a POST request. In some cases the spam bots are sent not only POST requests, but GET, ie loads pages a website and thereby overload the site with requests.</p>\r\n<p>Lowering effect occurs as a result of filtering spam traffic before download the main code of the site, which reduces the load on the CPU server and given off to the network traffic. Besides spam bots, also blocked all HTTP / HTTPS requests from spam active IP addresses. This helps to block HTTP DDoS attack, the SQL attack, scanning site, etc.</p>\r\n<p>Now in our database more than 20 million active spam IP addresses, 203 thousand networks, 30 thousand autonomous systems, 10 million email addresses, nearly 6 million e-mail domains. We block more than 2 million spam attacks per day, filtering accuracy above 99.998%.</p>\r\n<h2>The idea is to add anti-spam CleanTalk to tariffs for hosting free. In order,</h2>\r\n<ul>\r\n<li>to increase the value of hosting for your client, because for the same notional of $5, the client receives a larger set of services and gets free anti spam, which for all other costs $8 per year.</li>\r\n<li>to increase the loyalty to the hosting (since the transition to another hosting will be forced either to abandon a proven anti-spam services, or pay its full price on our website).</li>\r\n<li>to increase the number of customers per unit of equipment/bandwidth through the use of technology Spam FireWall.</li>\r\n</ul>\r\n<p>CleanTalk filters spam without any visible means of protection, i.e. CleanTalk not shifts the problem of spam on the shoulders of the visitor of the web site (as do the CAPTCHA solution).</p>\r\n<p>From our side we are ready to inform our customers about the opportunity to get free CleanTalk on your hosting account and your special offer, when moving the sites to your hosting. We will email a newsletter to all our customers (conversion to open 22-24%) and post your banner on this page.</p>\r\n<h2>Test as follows:</h2>\r\n<ol>\r\n<li>Register an account:&nbsp;<a class="daria-goto-anchor" href="http://cleantalk.org/register" target="_blank" data-vdir-href="https://mail.yandex.ru/re.jsx?uid=1130000011801443&amp;h=a,Y1PuB9pos0dbXVitdxplCw&amp;l=aHR0cDovL2NsZWFudGFsay5vcmcvcmVnaXN0ZXI" data-orig-href="http://cleantalk.org/register">http://cleantalk.org/regi<wbr />ster</a></li>\r\n<li>Add the IP address in the dashboard:&nbsp;</li>\r\n<li>Install on the site of one of the ready modules:&nbsp;</li>\r\n</ol>\r\n<p>Customers only have to install the plugin CleanTalk on the website and it will immediately work. CleanTalk provides support and customer service independently.</p>\r\n<p>&nbsp;</p>\r\n<h2>Our partners, providing CleanTalk for free</h2>\r\n<p>&nbsp;<a href="https://hostname.club/web-hosting/" target="_blank"><img src="../images/hostnameclub.png" alt="hostname.club" width="196" height="40" /></a></p>\r\n<p>&nbsp;</p>\r\n<p><strong>If you still have questions you can always ask us by&nbsp;mailing to&nbsp;<a href="mailto:welcome@cleantalk.org">welcome@cleantalk.org</a>.</strong></p>', 'cleantalk, partners, partnership, hostname.club', 'For Partners', '2017-10-25 11:42:21', NULL, NULL),
(319, 411, 'help', 'Делегирование сайтов другим пользователям', '<h1>Делегирование сайтов другим пользователям</h1>\n<p>Если вы хотите передать права на управление или просмотр ваших сайтов другому пользователю, то сделайте следующее:</p>\n<p>1. Войдите в Вашу <a href="/my">панель управления CleanTalk</a>.</p>\n<p>2. Нажмите на строку справа вверху, где указана ваша почта.</p>\n<p>3. Выберите пункт <strong>"Разрешения"</strong>.</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/articles/delegate/dashboard_grants_ru01.png" alt="Панель Управления &mdash; Разрешения" width="1157" height="429" /></p>\n<p>&nbsp;</p>\n<p>4. Нажмите на строку <strong>"Новое разрешение"</strong>.</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/articles/delegate/dashboard_grants_ru02.png" alt="Панель Управления &mdash; Новое разрешение" width="1157" height="305" /></p>\n<p>&nbsp;</p>\n<p>5. Выберите делегируемый сайт из выпадающего списка, выберите права для пользователя (Чтение или Чтение-И-Запись), напишите адрес почты доверенного пользователя.</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/articles/delegate/dashboard_grants_ru03.png" alt="Панель управления - Настройки разрешения" width="1157" height="438" /></p>\n<p>6. Нажмите кнопку <strong>"Сохранить"</strong>.</p>\n<p>7. Вернитесь в раздел <strong>"<a title="Разрешения" href="/my/grants">Разрешения</a>"</strong>. Теперь здесь отображается список делегированных сайтов.</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/articles/delegate/dashboard_grants_ru04.png" alt="Панель управления - Список разрешений" width="1157" height="330" /></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<h2>Где найти делегированные сайты в профиле доверенного пользователя?</h2>\n<p>1. Войдите в <a href="/my">панель управления CleanTalk</a> доверенного пользователя.</p>\n<p>2. На главной странице найдите заголовок <strong>"Разрешённые сайты"</strong>.</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/articles/delegate/dashboard_grants_ru05.png" alt="Панель управления - Разрешённые сайты" width="1157" height="414" /></p>\n<p>3. Делегированные сайты настраиваются точно так же, как и обычные.</p>\n<p>&nbsp;</p>\n<p><br /><strong>Если у вас остались вопросы вы всегда можете связаться со службой технической поддержки создав обращение <a href="/my/support">https://cleantalk.org/my/support</a>.</strong></p>', 'cleantalk, делегирование, права, передать права, передача прав, пользователь, другой пользователь', 'Делегирование сайтов другим пользователям', '2017-10-25 11:42:21', NULL, NULL),
(45, 143, 'help', 'What Type of License Use for WordPress Multisite Environment (WPMU)?', '<p>&nbsp;</p>\n<p>&nbsp;</p>\n<h1>What Type of License to Use for WordPress Multisite Environment (WPMU)?</h1>\n<p>&nbsp;</p>\n<p>Depending on your needs we offer 3 versions of WPMU packages.</p>\n<p>&nbsp;</p>\n<h4>1. Separate access keys for each website.</h4>\n<p>This package suits you if you want to have separate anti-spam statistics, black lists and options for each website.</p>\n<blockquote>\n<p><br />1.1. Choose a tariff which corresponds to your number of websites in WPMU. <a href="/register">Register</a> to the service. Buy the licence and <a href="/my/service?action=new">add all websites to your CleanTalk Control Panel</a>. Then you have to put each access key in each installed plugin for every website or give them to the webmasters and they will put the keys themselves. This method is convenient for those who don&rsquo;t have many websites and you want to give your clients a free anti-spam service. Furthermore, you can grant access to the added websites&rsquo; settings for your users in your CleanTalk profile using the feature &ldquo;Delegation&rdquo;.</p>\n<p><br />1.2. Let your users make payments for the anti-spam licence. It is necessary for you to <a href="/register">create an account</a> and choose an appropriate tariff. This method is convenient for those who want their users to make payments for the licence, to have their own anti-spam statistics, black lists and website settings.<br />&nbsp;</p>\n</blockquote>\n<h4>&nbsp;</h4>\n<h4>2. One access key for all WPMU websites.</h4>\n<p>You can use the same access key for all websites. In this case anti-spam statistics, black lists and your CleanTalk settings will be identical for all websites. <a href="/register">Register</a> to the service and choose a one website package. Copy the access key and put it in each CleanTalk plugin in each website.</p>\n<h4>&nbsp;</h4>\n<h4><br />3. Hosting package.</h4>\n<p>The most convenient method. In this case anti-spam service works using IP-address. By adding your IP-address to your CleanTalk Control Panel you protect all your websites where you have installed and activated the anti-spam plugin without using any access key. To setup your Hosting Package <a href="/register">register</a> to the service, go to your Control Panel and <a href="/my?cp_mode=hosting-antispam">switch to the Hosting Dashboard</a>. Follow the instruction and <a href="/my/hoster-ip?action=new">add your IP-address</a>. Then install and activate the anti-spam plugin on each website. As the result anti-spam statistics, black lists and website settings will be the same for all websites.</p>\n<p>If it is necessary for you to have separate anti-spam statistics, black lists and website settings then your users have to get their own access key. They have to create their CleanTalk accounts and get the keys. No additional payments are required.</p>\n<p>&nbsp;</p>\n<p>You can see the pricing <a href="/price">here</a>.</p>', 'wpmu, cleantalk, licence, multisite, wordpress', 'Type of License for WordPress Multisite Environment (WPMU)?', '2017-10-25 11:42:21', NULL, NULL),
(47, 145, 'help', 'Can I Have an Invoice from Cleantalk With VAT?', '<h2>Can I Have an Invoice from Cleantalk with VAT?</h2>\n<p>CleanTalk&nbsp;doesn''t offer invoices with VAT because of we are an Unites states company, we work without VAT.</p>\n<p>If you have questions you may contact us via mail&nbsp;<a href="mailto:welcome@cleantalk.org">welcome@cleantalk.org</a></p>\n<p>Or make a support ticket&nbsp;if you are registred user&nbsp;<a href="/my/support">https://cleantalk.org/my/support</a>.</p>', 'cleantalk, invoice, VAT', 'Invoice from Cleantalk with VAT', '2017-12-08 10:33:53', NULL, NULL),
(49, 147, 'help', 'Использование персональных черных списков Cleantalk', '<h1>Инструкция использования персональных черных списков CleanTalk</h1>\n<p>Персональные черные / белые списки&nbsp;помогают вам блокировать нежелательных пользователей или разрешать&nbsp;действия пользователей,&nbsp;которые были распознаны&nbsp;как спамеры.</p>\n<p>Запись в персональные черные / белые списки происходит&nbsp;каждый раз, когда вы отмечаете запрос в журнале вашего сервиса как "Спам" или "Не спам".</p>\n<p>Персональные списки - очень гибкий и одновременно мощный инструмент: вы можете заблокировать или разрешить IP-адрес, email или маску электронной почты (*@mail.com - заблокирует любой адрес заканчивающийся на @mail.com).</p>\n<p>Также&nbsp;вы можете заблокировать целую страну. Допустим,&nbsp;если у вас русскоязычный сайт и вы получаете массу спама из Индии, вы можете просто добавить Индию в черный список, и любой IP-адрес из этой страны будет заблокирован.</p>\n<p>Конечно,&nbsp;их нужно использовать очень аккуратно, продумывая все возможные последствия. Если у вас есть сомнения, то лучше проконсультироваться с нашим специалистом создав обращение в техническую поддержку&nbsp;<a href="/my/support">https://cleantalk.org/my/support</a>.</p>\n<p>Используйте нижеследующую инструкцию, чтобы снять часть вопросов по использованию.</p>\n<p>&nbsp;</p>\n<h2>Для&nbsp;использования персональных черных / белых списков:</h2>\n<p><strong>1</strong>. Вы должны перейти на страницу персональных списков для конкретного сервиса:</p>\n<h2><img src="https://s3.amazonaws.com/cleantalk-screenshots/CleanTalk''s+Personal+Blacklist-ru/Dashboard-ru.png" alt="" width="1000" height="371" /></h2>\n<p>&nbsp;</p>\n<h2>На странице черных / белых списков вы можете:</h2>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/CleanTalk''s+Personal+Blacklist-ru/blacklist-ru.png" alt="" width="1000" height="562" /></p>\n<p><strong>2.</strong> Добавить новый фильтр, нажав на<strong> кнопку (1): "Добавить новый фильтр"</strong>.&nbsp;Варианты ввода: <strong>IP адрес, почтовый адрес, страна, домен, стоп слово, почтовый адрес с маской</strong> (например: *@mail.com).</p>\n<p>Добавляйте ваши записи в появившееся <strong>поле 6</strong></p>\n<p>2.1 Чтобы <strong>разрешить</strong> или <strong>запретить</strong>&nbsp;введенные адреса, нажмите на кнопку "<strong>Разрешено</strong>" или "<strong>Запрещено</strong>".</p>\n<p>2.2 Добавилась расширенная маска для почтовых адресов. Символ звёздочка "<strong>*</strong>" обозначает любой набор символов.</p>\n<blockquote>\n<p>Примеры написания:</p>\n<p>&nbsp;&bull; <strong>name*@example.com</strong>&nbsp;(будут блокироваться все адреса с любым набором символов после "<strong>name</strong>").</p>\n<p>&nbsp;&bull; <strong>*aa44@example.com</strong>&nbsp;(будут блокироваться все адреса с любым набором символов перед "<strong>aa44</strong>").</p>\n<p>&nbsp;&bull; <strong>*kkk*vvv*@example.com</strong>&nbsp;(будут блокироваться все адреса с любым набором символов перед "<strong>kkk</strong>", перед "<strong>vvv</strong>" и после "<strong>vvv</strong>").</p>\n<p>&nbsp;&bull; И так далее, комбинации заданных наборов символом в адресе можно перемешивать в любом порядке.</p>\n</blockquote>\n<p>&nbsp;<img src="https://s3.amazonaws.com/cleantalk-screenshots/CleanTalk''s+Personal+Blacklist-ru/field.PNG" alt="" width="1000" height="490" /></p>\n<p><strong>3. Добавить все IP адреса страны, </strong>выбрав фильтр "<strong>Страна</strong>"</p>\n<p>3.1. В появившемся окне выбрать страну из списка.</p>\n<p>3.2. Разрешить или запретить&nbsp;пользователям из выбранных стран, нажав кнопки "<strong>Разрешено"</strong> или "<strong>Запрещено" </strong>/ "<strong>Разрешить все страны кроме выбранных</strong>" или "<strong>Запретить все страны кроме выбранных</strong>".</p>\n<p>&nbsp;<img src="https://s3.amazonaws.com/cleantalk-screenshots/CleanTalk''s+Personal+Blacklist-ru/country-ru.png" alt="" width="1000" height="601" /></p>\n<p><strong>4. Загрузить CSV файл</strong> с адресами и разрешениями, нажав <strong>кнопку 2</strong>.</p>\n<p>4.1. <strong>Скачать CSV файл</strong> с запрещенными и разрешенными адресами, нажав<strong>&nbsp;иконку 5.</strong></p>\n<p>&nbsp;</p>\n<p><strong>5. Удалить ранее введенные блокировки/разрешения.</strong></p>\n<p>5.1. Отметив их<strong> галочками 3</strong> и удалить <strong>кнопкой 4</strong>.</p>\n<p>&nbsp;</p>\n<p><br /><strong>6.</strong> Точно таким же образом Вы можете настроить услугу <strong>SpamFireWall</strong>. Пошаговая инструкция здесь: <a href="/help/sfw-blocks-networks">https://cleantalk.org/help/sfw-blocks-networks</a></p>\n<p>&nbsp;</p>\n<p><strong>Если у вас остались вопросы, Вы всегда можете связаться со службой технической поддержки, создав обращение:&nbsp;<a href="/my/support" target="_blank">https://cleantalk.org/my/support</a>.</strong></p>', 'черные списки, cleantalk, инструкция, использование', 'Персональные черные списки Cleantalk', '2018-02-28 14:23:22', NULL, NULL),
(515, 623, 'tour', 'Example page title', '<p>The poet instinctively felt the advantages of a real oral performance of those verses in which obscene idioms phonetically annihilated amphibrachias. The different arrangement elegantly alliterates abstract art, thus in some cases refrains, ring compositions, anaphores are formed. The idea, without taking into account the number of syllables that stand between the stresses, dissonves the mythopoetic chronotope. As was shown above, the location of the episodes is firmly an existential origin. Yamb, as I. Halperin rightly believes, is theoretically possible.</p>\r\n<p>Pastish is a lyrical cycle. Indirectly direct speech perfectly leads the flow of consciousness, although in this example it is impossible to judge author''s estimates. The subtext, in the first approximation, precisely annihilates the cycle. The location of the episodes is well aware of the urban origin.</p>\r\n<p>Metonymy, after touching something with its main antagonist in poststructural poetics, begins the beginning. Casuistry, without using formal features of poetry, integrates the mythological brahikatalectic verse. As was shown above, the stropoid annihilates the rhythmic pattern. Decoding integrates the story style. Abstract art, without using formal features of poetry, spatially enlightens the epithet. The polyphonic novel is alliterated by the simulacrum.</p>\r\n<p>The poet instinctively felt the advantages of a real oral performance of those verses in which obscene idioms phonetically annihilated amphibrachias. The different arrangement elegantly alliterates abstract art, thus in some cases refrains, ring compositions, anaphores are formed. The idea, without taking into account the number of syllables that stand between the stresses, dissonves the mythopoetic chronotope. As was shown above, the location of the episodes is firmly an existential origin. Yamb, as I. Halperin rightly believes, is theoretically possible.</p>\r\n<p>Pastish is a lyrical cycle. Indirectly direct speech perfectly leads the flow of consciousness, although in this example it is impossible to judge author''s estimates. The subtext, in the first approximation, precisely annihilates the cycle. The location of the episodes is well aware of the urban origin.</p>\r\n<p>Metonymy, after touching something with its main antagonist in poststructural poetics, begins the beginning. Casuistry, without using formal features of poetry, integrates the mythological brahikatalectic verse. As was shown above, the stropoid annihilates the rhythmic pattern. Decoding integrates the story style. Abstract art, without using formal features of poetry, spatially enlightens the epithet. The polyphonic novel is alliterated by the simulacrum.</p>\r\n<p>The poet instinctively felt the advantages of a real oral performance of those verses in which obscene idioms phonetically annihilated amphibrachias. The different arrangement elegantly alliterates abstract art, thus in some cases refrains, ring compositions, anaphores are formed. The idea, without taking into account the number of syllables that stand between the stresses, dissonves the mythopoetic chronotope. As was shown above, the location of the episodes is firmly an existential origin. Yamb, as I. Halperin rightly believes, is theoretically possible.</p>\r\n<p>Pastish is a lyrical cycle. Indirectly direct speech perfectly leads the flow of consciousness, although in this example it is impossible to judge author''s estimates. The subtext, in the first approximation, precisely annihilates the cycle. The location of the episodes is well aware of the urban origin.</p>\r\n<p>Metonymy, after touching something with its main antagonist in poststructural poetics, begins the beginning. Casuistry, without using formal features of poetry, integrates the mythological brahikatalectic verse. As was shown above, the stropoid annihilates the rhythmic pattern. Decoding integrates the story style. Abstract art, without using formal features of poetry, spatially enlightens the epithet. The polyphonic novel is alliterated by the simulacrum.</p>', '', '', '2017-11-01 14:47:58', NULL, NULL),
(51, 149, 'help', 'Cleantalk''s Personal Blacklists', '<h1>CleanTalk''s Personal Blacklist Manual</h1>\n<p><span style="font-weight: 400;">Personal Blacklist or Whitelist helps you to block unwanted users and to allow users that were recognized as spammers.</span></p>\n<p><span style="font-weight: 400;">The entry in the personal blacklist or whitelist occurs every time when you mark the request in the log of your service as "Spam" or "Not spam".</span></p>\n<p><span style="font-weight: 400;">Personal Blacklists are very flexible and powerful tool, you can block or allow IP address, email address or mask e-mail (*@mail.com - will block/allow every address ending on @mail.com).</span></p>\n<p><span style="font-weight: 400;">Also you can block a whole country. For example, if you have a US site and you get a lot of spam from India or Germany, you can simply add it&nbsp;to blacklist. Every IP address from this country will be blocked.</span></p>\n<p><span style="font-weight: 400;">Of course, they must be used very carefully, thinking through all possible consequences. If you have doubts, it is best to consult with our specialist by creating a ticket to technical support <a href="/my/support">https://cleantalk.org/my/support</a>.</span></p>\n<p><span style="font-weight: 400;">Use the following instruction to remove some of the questions.</span></p>\n<p>&nbsp;</p>\n<h2><span style="font-weight: 400;">To use the personal Black/Whitelists:</span></h2>\n<p><span style="font-weight: 400;"><strong>1.</strong> You should <strong>enter the page with blacklist&nbsp;</strong>for a particular service:</span></p>\n<p>&nbsp;<img src="https://s3.amazonaws.com/cleantalk-screenshots/CleanTalk''s+Personal+Blacklist/Dashboard.PNG" alt="Entering to blacklist" width="1000" height="303" /></p>\n<p>&nbsp;</p>\n<h2><span style="font-weight: 400;">You could do the following things on this page:</span></h2>\n<p><span style="font-weight: 400;"><img src="https://s3.amazonaws.com/cleantalk-screenshots/CleanTalk''s+Personal+Blacklist/Personal_lists.PNG" alt="" width="1000" height="523" /></span></p>\n<p><span style="font-weight: 400;"><strong>2.</strong> <strong>You could add new filter</strong> by pressing the button <strong>"Add new filter" ( button 1) -&gt; "Select record type"</strong>. </span></p>\n<p><span style="font-weight: 400;">The following filters are available to you: <strong>IP address</strong>,<strong> email address</strong>, <strong>countries, domains, words,</strong> email address with mask (e.g.: *@mail.com).</span></p>\n<p><span style="font-weight: 400;">Add your records in the <strong>field</strong> <strong>6</strong>.</span></p>\n<p>2.1 You could <strong>allow</strong> or <strong>block</strong> the address you have entered by pressing buttons "<strong>Whitelist</strong>" or "<strong>Blacklist</strong>".</p>\n<p>2.2 A new advanced e-mail address mask is added. Symbol asterisk "*" means any set of characters.</p>\n<p>&nbsp;Examples:</p>\n<p>&nbsp; &bull; <strong>name*@example.com</strong>&nbsp;(all e-mail addresses with any set of characters after <strong>"name"</strong> will be blocked).</p>\n<p>&nbsp; &bull; <strong>*aa44@example.com</strong>&nbsp;(all e-mail addresses with any set of characters before&nbsp;<strong>"aa44"</strong> will be blocked).</p>\n<p>&nbsp; &bull; <strong>*kkk*vvv*@example.com</strong>&nbsp;(all e-mail addresses with any set of characters before <strong>"kkk"</strong>, before <strong>"vvv"</strong> and after <strong>"vvv"</strong> will be blocked).</p>\n<p>&nbsp; &bull; And so on, you can use any combinations of characters and asterisks to make a mask.</p>\n<p><span style="font-weight: 400;"><img src="https://s3.amazonaws.com/cleantalk-screenshots/CleanTalk''s+Personal+Blacklist/adding.PNG" alt="" width="1000" height="538" /></span></p>\n<p><span style="font-weight: 400;"><strong>3.</strong> Add all of the <strong>IPs addresses in blacklist of choosen filter "Country".</strong><br /></span></p>\n<p><span style="font-weight: 400;">3.1. <strong>Select a country</strong> from the list in the window that apperars.</span></p>\n<p><span style="font-weight: 400;">3.2. Block or allow all IPs from selected countries <strong>by clicking &ldquo;Blacklist&rdquo; or &ldquo;Whitelist&rdquo;</strong> or choose "<strong>Blacklist/Whitelist all countries except selected</strong>"<br /></span></p>\n<p>&nbsp;<img src="https://s3.amazonaws.com/cleantalk-screenshots/CleanTalk''s+Personal+Blacklist/country.PNG" alt="" width="1000" height="568" /></p>\n<p>&nbsp;</p>\n<p><span style="font-weight: 400;"><strong>4.</strong> <strong>You could upload a SCV file</strong> with addresses and permissions by <strong>selecting it&nbsp;by&nbsp;button 2</strong>.</span></p>\n<p>4.1.<strong>&nbsp;You could download SCV file</strong> with all your blacklist and whitelist entries. <strong>By hitting icon 5</strong>.</p>\n<p>&nbsp;</p>\n<p><span style="font-weight: 400;"><strong>5.</strong> <strong>You could delete previously entered permissions</strong>.</span></p>\n<p>5.1. <strong>Select the entries</strong> you want to delete (<strong>checkbox 3</strong>) and<strong style="font-weight: 400;">&nbsp;</strong><strong>delete</strong> it by&nbsp;<strong>button 4</strong>.</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p><strong>6.</strong> The same way the service <strong>SpamFireWall</strong> could be setup. The&nbsp;step-by-step manual is here: <a href="/help/sfw-blocks-networks">https://cleantalk.org/help/sfw-blocks-networks</a></p>\n<p>&nbsp;</p>\n<p><strong>If you still have questions you can always ask our Tech support by&nbsp;creating a ticket&nbsp;<a href="/my/support">https://cleantalk.org/my/support</a>.</strong></p>', 'instruction, manual, blacklist, whitelist', 'Cleantalk''s Personal Blacklist', '2018-02-28 11:32:27', NULL, NULL),
(53, 151, 'help', 'CleanTalk SpamFireWall', '<h1 style="text-align: center;">CleanTalk SpamFireWall to Block Spam Traffic on Your Websites</h1>\n<p>The CleanTalk SpamFireWall manages and filters all inbound HTTP traffic to protect websites from spam bots and to reduce the load on the web servers. SpamFireWall is an additional and free option in the CleanTalk anti-spam plugins. <br /><br />Solution to minimize in-house development and maintenance as much as possible while reducing the load on servers. You can be up and run within 60 seconds. Blocks of Spam Bots before it reaches your web server. <br /><br /></p>\n<div align="center">\n<h1>How It Works</h1>\n</div>\n<p>Unlike other anti-spam solutions, CleanTalk SpamFireWall is able to reduce the load placed on your existing web server. The reporting allows administrators to control the process and to be sure of the accuracy of the work. <br /><br /></p>\n<ul>\n<li>Our servers use the cloud-based technology of all websites to fight spam.</li>\n<li>The visitor enters to your website.</li>\n<li>HTTP request data is checked for the nearly 2 million of certain IP spambots</li>\n<li>If it is an active spam bot, it will get a blank page, if it is a visitor then it proceeds to the site. That is completely transparent to the visitors.</li>\n<li>Parameters are written to the log which can be viewed in the CleanTalk Dashboard.</li>\n</ul>\n<p style="text-align: center;"><img src="../images/sfwan1.png" alt="" width="783" height="600" /></p>\n<div align="center">\n<h1>SpamFireWall Log</h1>\n</div>\n<p>All the CleanTalk SpamFireWall activity is being logged in the process of filtering. You will always have information about an IP address, date and time of each block spam attack.</p>\n<p style="text-align: center;"><img src="../images/sfwan2.png" alt="" width="1025" height="600" /></p>\n<p style="text-align: center;">&nbsp;</p>\n<h3 style="text-align: center;">CleanTalk SpamFireWall options</h3>\n<p style="text-align: center;"><iframe src="//www.youtube.com/embed/Eu5Zqryvj4k" width="560" height="314" allowfullscreen="allowfullscreen"></iframe></p>\n<p style="text-align: center;">&nbsp;</p>\n<div align="center">\n<h1>How SpamFireWall Can Help Reduce the Load on Web Server</h1>\n</div>\n<p>Since spam bots are blocked before they have access to your site, it can significantly reduce the load on your server. SpamFireWall allows blocking the most active spam bots before they get access to a website. It prevents loading of pages of the website by spam bots, so your web server doesn''t need to perform all scripts on these pages. Also, it prevents scanning of pages of the website spambots. Therefore SpamFireWall significantly can reduce the load on your web server. <br /><br />CleanTalk offers a risk-free 7-day evaluation of its services. Be up and running in 60 seconds!</p>\n<p>&nbsp;</p>\n<p>To test if SpamFireWall is working just add&nbsp;/?sfw_test_ip=10.10.10.10. Example</p>\n<p><strong><a href="http://www.yoursitename.domain/?sfw_test_ip=10.10.10.10">www.yoursitename.domain/?sfw_test_ip=10.10.10.10</a>&nbsp; </strong>(you must see blocking screen)</p>\n<p>&nbsp;</p>\n<div align="center"><a class="button --blue create_button" href="/register" rel="nofollow">Create account</a></div>', 'spamfirewall, block, ip, email, traffic', 'CleanTalk SpamFireWall to Block Spam Traffic on Your Websites', '2018-02-02 15:17:43', NULL, NULL),
(55, 153, 'help', 'Cleantalk SpamFireWall', '<h1 style="text-align: center;">CleanTalk SpamFireWall - решение для блокировки спам траффика на веб-сайтах</h1>\n<p>CleanTalk SpamFireWall фильтрует весь входящий траффик для защиты веб-сайтов от спам ботов - параллельно уменьшая нагрузку на веб-сервера. <br /><br />Установите данное решение за 60 секунд. Блокируйте спам-ботов до того, как они доберутся до Вашего сайта.<br /><br /></p>\n<div align="center">\n<h1>Как это работает</h1>\n</div>\n<p>В отличие от других решений Cleantalk SpamFirewall уменьшает нагрузку на Ваш веб-сервер. Детализированные отчёты дают полную картину происходящего с Вашим сайтом. <br /><br /></p>\n<ul>\n<li>Наш сервис использует облачные технологии для борьбы со спамом.</li>\n<li>Пользователь заходит на сайт.</li>\n<li>Данные HTTP запроса проверяются по базе данных, содержащей записи о более чем 2-х миилионах IP спам-ботов.</li>\n<li>Если IP содержится в базе спам-ботов, то показывается пустая страница. Для легитимных пользователей ничего не происходит - защита работает незаметно.</li>\n<li>Вся информация о процессе сохраняется в базе данных и доступна в панели управления.</li>\n</ul>\n<p style="text-align: center;"><img src="../images/sfwan1.png" alt="" width="783" height="600" /></p>\n<div align="center">\n<h1>Статистика SpamFireWall</h1>\n</div>\n<p>Все данные о процессе фильтрации записываются в журнал. Информация об IP адресе атакующего, дате и времени инцидента всегда доступна в панели управления.</p>\n<p style="text-align: center;"><img src="../images/sfwan2.png" alt="" width="1025" height="600" /></p>\n<div align="center">\n<h1>Как SpamFireWall может уменьшить нагрузку на Ваш веб-сервер</h1>\n</div>\n<p>Ввиду того,что спамботы блокируются до того, как произойдёт загрузка страниц Вашего сайта, нагрузка на Ваш веб-сервер может существенно уменьшиться.&nbsp;SpamFireWall блокирует наиболее активных спамботов до того, как они загрузят страницы Вашего сайта - оставляя Вашему веб-серверу больше ресурсов для обработки легитимных запросов.</p>\n<p><br /><br />CleanTalk предлагает Вам пробный бесплатный 14-дневный период. Запусти защиту от спама за 60 секунд!&nbsp;<br /><br /></p>\n<p>Чтобы проверить работу&nbsp;SpamFireWall добавьте&nbsp;/?sfw_test_ip=10.10.10.10 к адресу сайта. Например,</p>\n<p><a href="http://www.имясайта.домен/?sfw_test_ip=10.10.10.10">www.имясайта.домен/?sfw_test_ip=10.10.10.10</a>&nbsp;(вы должны увидеть экран блокировки)</p>\n<p>&nbsp;</p>\n<div align="center"><a class="button --blue create_button" href="/register" rel="nofollow">Создать акаунт</a></div>', 'spamfirewall, спамфервол, блокировать, ip, email', 'CleanTalk SpamFireWall - решение для блокировки спам траффика на веб-сайтах', '2017-12-27 10:32:18', NULL, NULL),
(57, 155, 'help', 'Как оплатить сервис CleanTalk с помощью WebMoney?', '<h1>Как оплатить сервис CleanTalk с помощью WebMoney?</h1>\n<p>Что бы&nbsp;оплатить сервис CleanTalk с помощью WebMoney, вам нужно:</p>\n<p>1. Перейти на <a href="/my/bill/recharge">страницу оплаты</a>.</p>\n<p>2. Выбрать плтежный шлюз "PAYANYWAY".</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/dashboard_ru/bill_recharge/bill_PAYANYWAY_payNow.png" alt="Оплата Cleantalk с WebMoney" width="1000" height="582" /></p>\n<p>3.1. Выбрать в левом меню пункт "Электронные деньги".</p>\n<p>3.2. В появившемся меню выбрать "WebMoney".</p>\n<p>3.3. Использовать один из предложенных способов авторизации.</p>\n<p>3.4.&nbsp;Продолжить.</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/dashboard_ru/bill_recharge/PAYANYWAY/webMoney_instructions.png" alt="Оплата cleantalk - PAYANYWAY - WebMoney" width="1000" height="568" /></p>\n<p>&nbsp;</p>\n<div>Если возникнут какие-либо вопросы, то Вы можете задать их через:</div>\n<div>&nbsp;&mdash; Почту [ support@cleantalk.org ].</div>\n<div>&nbsp;&mdash; Обращение [ <a href="/my/support" target="_blank">https://cleantalk.org/my/support</a> ].</div>\n<div>&nbsp;&mdash; Форум [ <a href="https://community.cleantalk.org/" target="_blank">https://community.cleantalk.org/</a> ].</div>\n<p>&nbsp;</p>\n<div class="button_go"><a class="button --blue" href="/my">Перейти в панель управления</a></div>\n<p>&nbsp;</p>\n<div class="grey_text">Посмотреть статус антиспама, добавить новый сайт или управлять существующими вы можете в Панели управления.</div>', 'webmoney, оплата', 'Оплата CleanTalk с помощью WebMoney', '2017-10-25 11:42:21', NULL, NULL),
(65, 163, 'help', 'Расширенный пакет опций CleanTalk', '<h1 style="text-align: center;">Расширенный пакет&nbsp;опций CleanTalk</h1>\n<p>&nbsp;</p>\n<p>В дополнение к анти-спам сервису, CleanTalk предоставляет расширенные возможности сервиса:</p>\n<h2 style="text-align: left;">1. 45&nbsp;дней журнала</h2>\n<p>Хранение детализированной истории запросов в течение 45 дней. <strong>Если вам недостаточно 7 дней</strong> для хранения истории, CleanTalk может <strong>увеличить этот срок до 45 дней</strong>. Эти данные полезны, если вы потеряете сообщение, для обратной связи и просмотра подробной информации по каждому запросу.&nbsp;</p>\n<p>Журнал можно найти здесь:&nbsp;<a href="/my/show_requests">https://cleantalk.org/my/show_requests</a></p>\n<p>&nbsp;</p>\n<h2 style="text-align: left;">2. Фильтрация по стоп-словам</h2>\n<p><strong>Фильтрация сообщений, содержащих стоп-слова</strong>, для запрета комментариев, содержащих слова, внесенные вами в словарь стоп-слов. Эта функция необходима, если вы хотите запретить комментарии, содержащие ненормативную лексику, оскорбления и т.д.&nbsp;</p>\n<p>Добавить стоп-слова вы можете здесь:&nbsp;<a href="/my/stop_list">https://cleantalk.org/my/stop_list</a></p>\n<p>&nbsp;</p>\n<h2 style="text-align: left;"><a id="blacklist_country"></a>3. Чёрный список стран</h2>\n<p>Фильтрация сообщений/регистраций по странам, позволяет блокировать все комментарии/регистрации пользователям из отмеченных вами стран.</p>\n<p>Управление персональными чёрными списками здесь:&nbsp;<a href="/my/show_private">https://cleantalk.org/my/show_private</a></p>\n<p>&nbsp;</p>\n<h2 style="text-align: left;">4. Сообщение о причинах фильтрации</h2>\n<p>Сообщение о причинах фильтрации посетителям сайта позволяет заменить&nbsp;текст ответа сервера для пользователей. Например, по умолчанию пользователи видят вот такое сообщение: "<strong>Запрещено. Пожалуйста, включите JavaScript.</strong>". Вы можете изменить его на: "<strong>Ваше сообщение заблокировано, пожалуйста, свяжитесь с администратором по почте mail@mail.mail.</strong>". Это&nbsp;снизит риск ложных срабатываний.</p>\n<p>Настроить сообщение можно здесь:&nbsp;<a href="/my/service?action=edit">https://cleantalk.org/my/service?action=edit</a></p>\n<p>&nbsp;</p>\n<h2 style="text-align: left;">5. Делегирование доступа к веб-сайтам</h2>\n<p>Передача прав на управление или просмотр ваших сайтов другому пользователю.</p>\n<p>Делегирование прав можно сделать здесь:&nbsp;<a href="/my/grants">https://cleantalk.org/my/grants</a></p>\n<p>&nbsp;</p>\n<div>&nbsp;</div>\n<div><br />После&nbsp;покупки расширенного пакета он действителен для всех сайтов на вашем аккаунте.</div>\n<div>Во время ознакомительного периода все функции доступны для тестирования без ограничений.</div>\n<div>&nbsp;</div>\n<div>Эти дополнительные возможности делают CleanTalk более гибким инструментом и усиливают&nbsp;защиту от спам-ботов.</div>\n<div>&nbsp;</div>\n<div>&nbsp;</div>\n<div style="text-align: center;"><a href="/my/bill/recharge?package=1"><img src="../images/extendedpack.png" alt="" width="400" height="50" /></a></div>\n<div style="text-align: center;">&nbsp;</div>', 'Расширенный пакет, сервис, антиспам', 'Расширенный пакет опций CleanTalk', '2017-10-25 11:42:21', NULL, NULL),
(63, 161, 'help', 'CleanTalk Extra Package', '<h1 style="text-align: left;">CleanTalk Extra Package</h1>\n<p>In addition to the anti-spam service CleanTalk provides advanced service:</p>\n<h2 style="text-align: left;">1. 45 days log</h2>\n<p>Storage of detailed history of the request 45 days. If you have<strong> not enough storage for 7 days</strong> of history, CleanTalk can <strong>extend this to 45 days</strong>. These data are useful if you lose your a message, for feedback and view detailed information about each request.</p>\n<p>You could find log here:&nbsp;<a href="/my/show_requests">https://cleantalk.org/my/show_requests</a></p>\n<p>&nbsp;</p>\n<h2 style="text-align: left;">2. Stop words</h2>\n<p><strong>Filtering messages containing "stop words"</strong>, to disallow comments that contain the word you have added to the dictionary "stop words." This function is required if you want to disable comments containing not normative lexicon, insults, etc.</p>\n<p>Add "stop words" you can here <a class="daria-goto-anchor" href="/my/stop_list" target="_blank" data-vdir-href="https://mail.yandex.ru/re.jsx?uid=1130000011801443&amp;h=a,VrDTGmdhoDOZYYzupCksaw&amp;l=aHR0cHM6Ly9jbGVhbnRhbGsub3JnL215L3N0b3BfbGlzdA" data-orig-href="https://cleantalk.org/my/stop_list">https://cleantalk.org/my/<wbr />stop_list</a>.</p>\n<p>&nbsp;</p>\n<h2 style="text-align: left;"><a id="country_blacklist"></a>3. Country blacklist</h2>\n<p>Filtering posts/registrations by country allows you to block all comments/registration to users from selected countries.</p>\n<p>You could set personal blacklist here:&nbsp;<a href="/my/show_private">https://cleantalk.org/my/show_private</a></p>\n<p>&nbsp;</p>\n<h2 style="text-align: left;">4. Message to forbidden visitors</h2>\n<p>Message to forbidden visitors allows you to customise an answer form our servers. By default they will get messages like: "<strong>Forbidden. Please enable JavaScript</strong>".</p>\n<p>You could change it with this feature for example: "<strong>Your message was blocked. Please contact administrator via mail@mail.mail</strong>". It will lower risk of false positives.</p>\n<p>You could set it here:&nbsp;<a href="/my/service?action=edit">https://cleantalk.org/my/service?action=edit</a></p>\n<p>&nbsp;</p>\n<h2 style="text-align: left;">5. Website access delegation</h2>\n<p>The possibility to grant the rights to other users to manage or inspect your websites.</p>\n<p>Website delegation can be setup here: <a href="/my/grants">https://cleantalk.org/my/grants</a></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>When you purchase an extra package, it is valid for all the sites in your account. For the trial period all features are available for testing without restrictions.</p>\n<p>These additional features make CleanTalk more flexible instrument and complement the protection from spam bots of new features.</p>\n<p>&nbsp;</p>\n<div style="text-align: center;">&nbsp;<a href="/my/bill/recharge?package=1"><img src="http://cleantalk.org/images/extendedpacken.png" alt="" width="351" height="50" /></a></div>', 'extra package, service, anti-spam', 'CleanTalk Extra Package', '2017-10-25 11:42:21', NULL, NULL),
(67, 165, 'help', 'Is CleanTalk Compatible with CloudFlare?', '<h1>Is CleanTalk Compatible with CloudFlare?</h1>\r\n<p>CleanTalk is fully compatible with CloudFlare. Service doesn''t filter CloudFlares IP''s (AS13335) over blacklists database, so in this way plugin/service filters spam bots uses other anti spam tests.</p>', 'cloudflare, compatibility', 'CleanTalk Compatible with CloudFlare', '2017-10-25 11:42:21', NULL, NULL),
(69, 167, 'help', 'CleanTalk и CloudFlare совместимы?', '<h1>CleanTalk&nbsp;и CloudFlare совместимы?</h1>\r\n<p>CleanTalk является полностью совместимым&nbsp;с&nbsp;CloudFlare. Сервис&nbsp;не фильтрует&nbsp;IP-адреса&nbsp;CloudFlare&nbsp;(AS13335) по&nbsp;черным спискам&nbsp;базы данных, таким образом плагин/сервис использует&nbsp;другие способы&nbsp;фильтрации&nbsp;спам ботов.</p>', 'cloudflare, совместимость', 'Совместимость CleanTalk и CloudFlare', '2017-10-25 11:42:21', NULL, NULL),
(71, 169, 'help', 'О версии плагина в панели управления CleanTalk', '<h1>О версии&nbsp;плагина в панели управления CleanTalk</h1>\r\n<p>Информация о версии плагина/приложения в панели управления CleanTalk берется из его&nbsp;последнего запроса к серверам.<br />Таким образом после обновления на своем сайте вы можете просто подождать следующей регистрации или комментария на вашем сайте.&nbsp;Тестовый запрос может сделать и сам плагин когда вы нажимаете кнопку сохранения настроек. Это релизовано на следующих CMS:</p>\r\n<ul style="list-style-type: disc;">\r\n<li>Bitrix</li>\r\n<li>IPBoard</li>\r\n<li>IPS</li>\r\n<li>Joomla</li>\r\n<li>WordPress</li>\r\n</ul>\r\n<p>Версия в панели управления обновится в течении&nbsp;3 минут после получения запроса от вашего сайта.</p>', 'версия, панель управления, в аккаунте', 'Версия CleanTalk в панели управления', '2017-10-25 11:42:21', NULL, NULL);
INSERT INTO `articles` (`article_id`, `article_linkid`, `article_where`, `article_title`, `article_content`, `keywords`, `description`, `updated`, `widget`, `widget_url`) VALUES
(73, 171, 'help', 'Version of Plugin in the CleanTalk Dashboard', '<h1>Version&nbsp;of Plugin&nbsp;in the&nbsp;CleanTalk Dashboard</h1>\r\n<p><span>Information about the version of the plugin/app in the control panel CleanTalk is taken from his last request to the servers.<br />So after the updates on your website you can just wait for the next registration or comment on the website. The test request can be done by&nbsp;the plugin when you click save settings. It works&nbsp;on&nbsp;following CMS:</span></p>\r\n<ul style="list-style-type: disc;">\r\n<li>Bitrix</li>\r\n<li>IPBoard</li>\r\n<li>IPS</li>\r\n<li>Joomla</li>\r\n<li>WordPress</li>\r\n</ul>\r\n<p>The version in the control panel will be updated within 3 minutes after receipt of the request from your site.</p>', 'version, dashdoard', 'Version of CleanTalk in the Dashboard', '2017-10-25 11:42:21', NULL, NULL),
(75, 173, 'help', 'Is CleanTalk Better than Akismet?', '<h1 style="text-align: center;">Is CleanTalk Better than Akismet?</h1>\n<h3 style="text-align: center;">Lets take a look at сomparison table:</h3>\n<table style="width: 860px; border-color: black; margin-left: auto; margin-right: auto;" border="2px">\n<tbody>\n<tr style="height: 13px;">\n<td style="height: 13px; width: 84px;">&nbsp;</td>\n<td style="height: 13px; width: 84px; background-color: #3090c7; padding-top: 20px;">\n<h2 style="text-align: center;">CleanTalk</h2>\n</td>\n<td style="height: 13px; width: 84px; background-color: #3090c7; padding-top: 20px;">\n<h2 style="text-align: center;">Akismet</h2>\n</td>\n</tr>\n<tr style="height: 13px;">\n<td style="height: 13px; width: 84px; background-color: #3090c7;">\n<blockquote>\n<h3>Form protection</h3>\n</blockquote>\n</td>\n<td style="height: 13px; width: 329px; padding: 20px; vertical-align: middle; background-color: #00cc33;">\n<p style="text-align: center;"><strong>All web forms:</strong></p>\n<ul>\n<li>comments</li>\n<li>registrations</li>\n<li>contact</li>\n<li>orders forms</li>\n<li>Any others</li>\n</ul>\n</td>\n<td style="height: 13px; width: 412px; text-align: center; background-color: #cc3300;"><strong>Only Comments</strong></td>\n</tr>\n<tr style="height: 13.3281px;">\n<td style="height: 13.3281px; width: 84px; background-color: #3090c7;">\n<blockquote>\n<h3>Logs</h3>\n</blockquote>\n</td>\n<td style="height: 13px; width: 329px; padding: 20px; vertical-align: middle; background-color: #00cc33;">\n<p style="text-align: center;"><strong>From 7 to 45 day rejected or approved logs with details:</strong></p>\n<ul>\n<li>IPs</li>\n<li>Emails</li>\n<li>Countries</li>\n<li>Nicknames</li>\n<li>Rejection/approve reason</li>\n<li>URL with query</li>\n<li>And many others</li>\n</ul>\n<p>&nbsp;</p>\n</td>\n<td style="height: 13.3281px; width: 412px; text-align: center; background-color: #cc3300;">\n<blockquote>\n<p><strong>Only Statistics or&nbsp;Advanced Statistics</strong></p>\n<p><strong>No details</strong></p>\n</blockquote>\n</td>\n</tr>\n<tr style="height: 13.3281px;">\n<td style="height: 13.3281px; width: 84px; background-color: #3090c7;">\n<blockquote>\n<h3>Check existing&nbsp;users for spam</h3>\n</blockquote>\n</td>\n<td style="height: 13px; width: 329px; padding: 20px; vertical-align: middle; background-color: #00cc33;">\n<p style="text-align: center;"><strong>Yes</strong></p>\n</td>\n<td style="height: 13.3281px; width: 412px; text-align: center; background-color: #cc3300;">\n<p><strong>No</strong></p>\n</td>\n</tr>\n<tr style="height: 13px;">\n<td style="height: 13px; width: 84px; background-color: #3090c7;">\n<blockquote>\n<h3>SpamFireWall</h3>\n</blockquote>\n</td>\n<td style="height: 13px; width: 329px; padding: 20px; vertical-align: middle; background-color: #00cc33;">\n<p style="text-align: center;"><strong><span style="color: #000000;"><a style="color: #000000;" href="/help/cleantalk-spam-firewall" target="_blank">Unique feature</a></span> that stops spam bots before they access (read) your websites.</strong></p>\n<p style="text-align: center;">In this way your hosting server works faster and it''s better to website''s SEO results!</p>\n</td>\n<td style="height: 13px; width: 412px; text-align: center; background-color: #cc3300;"><strong>&nbsp;SpamFireWall? What is SpamFireWall?</strong></td>\n</tr>\n<tr style="height: 13px;">\n<td style="height: 13px; width: 84px; background-color: #3090c7;">\n<blockquote>\n<h3>Paid</h3>\n</blockquote>\n</td>\n<td style="height: 13px; width: 329px; padding: 20px; vertical-align: middle; background-color: #00cc33;">\n<p style="text-align: center;"><strong>$8.00&nbsp;for a&nbsp;year !</strong></p>\n<p style="text-align: center;">Tech support answers you within one hour.</p>\n</td>\n<td style="height: 13px; width: 412px; text-align: center; background-color: #ef7112;">\n<blockquote>\n<p><strong>Starts from 60$/year up to 600$ for business!</strong></p>\n<p><strong>Free for non commercial use (up to 50,000 monthly checks)</strong></p>\n</blockquote>\n</td>\n</tr>\n<tr style="height: 13px;">\n<td style="height: 13px; width: 84px; background-color: #3090c7;">\n<blockquote>\n<h3>Compability</h3>\n</blockquote>\n</td>\n<td style="height: 13px; width: 329px; padding: 20px; vertical-align: middle; background-color: #00cc33;">\n<p style="text-align: center;"><strong>Has plugin for <span style="color: #000000;"><a style="color: #000000;" href="/install" target="_blank">every popular CMS</a></span></strong></p>\n<p style="text-align: center;">If you have site+forum You can use CleanTalk on both of them!</p>\n</td>\n<td style="height: 13px; width: 412px; text-align: center; background-color: #cc3300;"><strong>Only Wordpress</strong></td>\n</tr>\n<tr style="height: 13px;">\n<td style="height: 13px; width: 84px; background-color: #3090c7;">\n<blockquote>\n<h3>Limitation for queries</h3>\n</blockquote>\n</td>\n<td style="height: 13px; width: 329px; padding: 20px; vertical-align: middle; background-color: #00cc33;">\n<p style="text-align: center;"><strong>Unlimited spam checks</strong></p>\n</td>\n<td style="height: 13px; width: 412px; text-align: center; background-color: #cc3300;"><strong>From 50''000 to 100''000</strong></td>\n</tr>\n<tr style="height: 13px;">\n<td style="height: 13px; width: 84px; background-color: #3090c7;">\n<blockquote>\n<h3>Personal blacklists/Stop words check/Country blacklists</h3>\n</blockquote>\n</td>\n<td style="height: 13px; width: 329px; padding: 20px; vertical-align: middle; background-color: #00cc33;">\n<p style="text-align: center;"><strong>Yes/Yes/Yes</strong></p>\n</td>\n<td style="height: 13px; width: 412px; text-align: center; background-color: #cc3300;"><strong>No/No/No</strong></td>\n</tr>\n</tbody>\n</table>\n<h3>&nbsp;</h3>\n<h3 style="text-align: center;">Akismet is definitely better if&nbsp;You don''t have $0.66 per month, You have small blog on Wordpress without followers.</h3>\n<h3 style="text-align: center;">I have already chosen CleanTalk.</h3>\n<h2 style="text-align: center;"><strong>Make your choice!</strong></h2>\n<p>&nbsp;</p>', 'comparison, better, akismet', 'Comparison cleantalk with akismet', '2017-10-25 11:42:21', NULL, NULL),
(77, 175, 'help', 'Why CleanTalk Doesn''t Stop Spam on JComments?', '<h1>Why CleanTalk Doesn''t Stop Spam on JComments?</h1>\n<p>It could happen if you don''t set Administator Panel -&gt; Components&nbsp;-&gt;&nbsp;JComments -&gt; Settings -&gt;&nbsp;<label id="jform_enable_plugins-lbl" class="hasTip" title="" for="jform_enable_plugins">Enable plugins</label>&nbsp;-&gt; "Yes".</p>\n<p>Please look at the picture to learn more about this:</p>\n<p><img src="https://s3.amazonaws.com/cleantalk-screenshots/articles/JComments.png" alt="JComments - Enable plugins - Yes" width="1000" height="740" /></p>\n<p>&nbsp;</p>\n<p><strong>If you still have questions you can always ask our Tech support by&nbsp;creating a ticket&nbsp;<a href="/my/support">https://cleantalk.org/my/support</a>.</strong></p>', 'jcomments, spam, through, miss, pass, let', 'Why CleanTalk Doesn''t Stop Spam on JComments?', '2017-10-25 11:42:21', NULL, NULL),
(79, 0, 'docs', 'Типы данных в БД и отчётах', '<p>Таблица <strong>services_private_list</strong></p>\n<p>Поле <strong>record_type</strong></p>\n<table style="height: 96px; width: 387px;" border="1" cellspacing="0" cellpadding="2">\n<tbody>\n<tr style="height: 12px;">\n<td style="width: 97.9097px; height: 12px;">1</td>\n<td style="width: 277.09px; height: 12px;">IP</td>\n</tr>\n<tr style="height: 12px;">\n<td style="width: 97.9097px; height: 12px;">2</td>\n<td style="width: 277.09px; height: 12px;">e-mail</td>\n</tr>\n<tr style="height: 12px;">\n<td style="width: 97.9097px; height: 12px;">3</td>\n<td style="width: 277.09px; height: 12px;">страна (country_XX)</td>\n</tr>\n<tr style="height: 12px;">\n<td style="width: 97.9097px; height: 12px;">4</td>\n<td style="width: 277.09px; height: 12px;">почтовый домен</td>\n</tr>\n<tr style="height: 12px;">\n<td style="width: 97.9097px; height: 12px;">5</td>\n<td style="width: 277.09px; height: 12px;">домен первого уровня</td>\n</tr>\n<tr style="height: 12px;">\n<td style="width: 97.9097px; height: 12px;">6</td>\n<td style="width: 277.09px; height: 12px;">сеть (n.n.n.n/n) SFW</td>\n</tr>\n<tr style="height: 12px;">\n<td style="width: 97.9097px; height: 12px;">7</td>\n<td style="width: 277.09px; height: 12px;">сеть (n.n.n.n/n) Security</td>\n</tr>\n<tr style="height: 12px;">\n<td style="width: 97.9097px; height: 12px;">8</td>\n<td style="width: 277.09px; height: 12px;">стоп-слово (=xxxxxxxxxx...)</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>American Express report</p>\n<table style="float: left;" border="1" cellspacing="0" cellpadding="2"><colgroup width="80"></colgroup> <colgroup width="161"></colgroup> <colgroup width="523"></colgroup> <colgroup width="80"></colgroup> <colgroup width="93"></colgroup>\n<tbody>\n<tr style="height: 21px;">\n<td style="height: 21px; width: 10px;" align="left" valign="bottom"><strong><span style="color: #000000;">Sr#</span></strong></td>\n<td style="height: 21px; width: 192px;" 